import React, { Component } from "react";
import { Card, Tooltip, Button } from "@material-ui/core";
import "../Cssfile/Displaynotes.css";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import { Dialog } from "@material-ui/core";
import { DisplayAllNotes, editNote, removeReminder } from "./Service";
import Showmoreoption from "./Showmoreoption";
import AddArchive from "./AddArchive";
import "../Cssfile/TrashNote.css";
import TextField from "@material-ui/core/TextField";
import SetColor from "./SetColor";
import AddReminder from "./AddReminder";
import Chip from "@material-ui/core/Chip";
import Collaborator from "./Collaborator";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import {Redirect} from 'react-router-dom'

export class Displaynotes extends Component {
  constructor(props) {
    super(props);

    let token = localStorage.getItem("Token");
    let loggedIn = true;
    if (token == null) {
      loggedIn = false;
    }
    this.state = {
      loggedIn,
      open: false,
      dialogOpen: false,
      more: false,
      allNote: [],
      notearr: null,
      title: "",
      description: ""
    };
  }

  handleUpdatetitle = event => {
    this.setState({
      title: event.target.value
    });
    console.log("ddd" + this.state.title);
  };

  handleUpdatedescription = event => {
    this.setState({
      description: event.target.value
    });
    console.log(this.state.description);
  };

  handleClose = () => {
    console.log("close ");
    this.setState({
      dialogOpen: false
    });

    let updatenote = {};
    console.log(this.state.title);
    console.log(this.state.description);

    updatenote.title = this.state.title;
    updatenote.description = this.state.description;
    let token = localStorage.getItem("Token");
    let noteid = this.state.notearr.id;

    editNote(updatenote, noteid, token)
      .then(Response => {
        console.log("update note");
        this.getAllNotes();
      })
      .catch(err => {
        console.log("not update note");
      });
  };

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {
        console.log(Response.data);
        let array = [];
        Response.data.data.forEach(element => {
          if (element.archive === true && element.trash === false) {
            array.push(element);
          }
        });
        this.setState({
          allNote: array
        });

        console.log("d" + this.state.allnote);
      })
      .catch(err => {
        console.log("error");
      });
  };

  handleDialogOpen = note => {
    console.log(note);
    this.setState({
      dialogOpen: !this.state.dialogOpen,
      notearr: note
    });
  };

  handleDelete = noteid => {
    let token = localStorage.getItem("Token");
    removeReminder(noteid, token)
      .then(Response => {
        console.log("remove reminder");
        this.getAllNotes();
      })
      .catch({
        // console.log("fail..");
      });
  };

  UNSAFE_componentWillMount() {
    this.getAllNotes();
  }

  render() {
    if (this.state.loggedIn === false) {
      return <Redirect to="/login" />;
    }



           <Snackbar
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'left',
          }}
          open={this.state.open}
          autoHideDuration={6000}
          onClose={this.handleClose}
          ContentProps={{
            'aria-describedby': 'message-id',
          }}
          message={<span id="message-id">Note archived</span>}
          action={[
            <Button key="undo" color="secondary" size="small" onClick={this.handleClose}>
              UNDO
            </Button>,
            <IconButton
              key="close"
              aria-label="Close"
              color="inherit"
              className={classes.close}
              onClick={this.handleClose}
            >
              <CloseIcon />
            </IconButton>,
          ]}
        />

    return (
      <div>
        <div
          style={{
            display: "flex",
            width: "800px",
            flexWrap: "wrap",
            flexDirection: "row",
            paddingTop: "11%",
            paddingLeft: "8%"
          }}
        >
          {this.state.allNote.map(note => (
            <div className="trashnoted" style={{ padding: 5 }}>
              <Card
                style={{
                  width: "222px",
                  boxShadow: "2px 2px 8px rgba(0,0,0,0.2)",
                  backgroundColor: note.color,
                  borderRadius: "10px"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between"
                  }}
                >
                  <div>
                    <TextField
                      onClick={() => this.handleDialogOpen(note)}
                      id="standard-full-width"
                      style={{ margin: 8 }}
                      value={note.title}
                      multiline
                      fullWidth
                      margin="normal"
                      InputProps={{
                        disableUnderline: true
                      }}
                    />
                  </div>
                  <div className="footerarea">
                    <Tooltip title="pin note ">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 24 24"
                      >
                        <path fill="none" d="M0 0h24v24H0z" />
                        <path
                          fill="#000"
                          d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                        />
                      </svg>
                    </Tooltip>
                  </div>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex"
                  }}
                  onClick={() => this.handleDialogOpen(note)}
                >
                  {" "}
                  <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    value={note.description}
                    multiline
                    fullWidth
                    margin="normal"
                    InputProps={{
                      disableUnderline: true
                    }}
                  />
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    flexDirection: "row"
                  }}
                >
                  {note.reminder != null ? (
                    <div
                      style={{
                        justifyContent: "spacearound",
                        display: "flex"
                      }}
                    >
                      <Chip
                        style={{ backgroundColor: "#rgba(206, 206, 206, 0)" }}
                        label={note.reminder}
                        onDelete={() => this.handleDelete(note.id)}
                        variant="outlined"
                      />
                    </div>
                  ) : null}
                  <div
                    style={{
                      justifyContent: "spacearound",
                      display: "flex",
                      paddingLeft: "6px"
                    }}
                  >
                    {note.collabrators != null
                      ? note.collabrators.map(col => (
                          <div>
                            <Tooltip title={col}>
                              <AccountCircleIcon />
                            </Tooltip>
                          </div>
                        ))
                      : null}
                  </div>
                </div>
                <div></div>

                <div className="footerarea">
                  <div
                    style={{
                      justifyContent: "space-around",
                      display: "flex",
                      padding: "12px",
                      paddingBottom: "13px"
                    }}
                  >
                    <AddReminder
                      propsRemindernote={note.id}
                      refresh={this.getAllNotes}
                    />
                    <Collaborator
                      propsRemindernote={note.id}
                      propscollbratorid={
                        note.collabrators !== null ? note.collabrators : null
                      }
                      refresh={this.getAllNotes}
                    />

                    <SetColor
                      propsColornote={note.id}
                      refresh={this.getAllNotes}
                    />
                    <AddArchive
                      propsarchivenote={note.id}
                      refresh={this.getAllNotes}
                    />
                    <Tooltip title=" More">
                      <Showmoreoption
                        propsdisplaynote={note.id}
                        refresh={this.getAllNotes}
                      />
                    </Tooltip>
                  </div>
                </div>
              </Card>
            </div>
          ))}

          <Dialog
            open={this.state.dialogOpen}
            onClose={this.handleClose}
            aria-labelledby="simple-dialog-title"
            style={{ width: "100%" }}
          >
            {this.state.notearr !== null ? (
              <div className="trashnoted" style={{ padding: 5 }}>
                <Card
                  style={{
                    width: "484px",
                    boxShadow: "2px 2px 8px rgba(0,0,0,0.2)",
                    backgroundColor: this.state.notearr.color
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between"
                    }}
                  >
                    <div>
                      <TextField
                        id="standard-full-width"
                        style={{ margin: 8 }}
                        defaultValue={this.state.notearr.title}
                        multiline
                        fullWidth
                        margin="normal"
                        InputProps={{
                          disableUnderline: true
                        }}
                        onChange={this.handleUpdatetitle}
                      />
                    </div>
                    <div className="footerarea">
                      <Tooltip title="pin note ">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          width="24"
                          height="24"
                          viewBox="0 0 24 24"
                        >
                          <path fill="none" d="M0 0h24v24H0z" />
                          <path
                            fill="#000"
                            d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                          />
                        </svg>
                      </Tooltip>
                    </div>
                  </div>
                  <div
                    style={{
                      justifyContent: "spacearound",
                      display: "flex"
                    }}
                  >
                    <TextField
                      id="standard-full-width"
                      style={{ margin: 8 }}
                      defaultValue={this.state.notearr.description}
                      multiline
                      fullWidth
                      margin="normal"
                      InputProps={{
                        disableUnderline: true
                      }}
                      onChange={this.handleUpdatedescription}
                    />
                  </div>
                  {/* {this.state.notearr.reminder !== null ? ( */}
                  {/* <div>

                     <Chip
                       style={{ backgroundColor: "#dedede" }}
                       label="Deletable Secondary Chip"
                       onDelete={this.handleDelete}
                       variant="outlined"
                     />
                   </div> */}
                  <div
                    style={{
                      justifyContent: "spacearound",
                      display: "flex"
                    }}
                  >
                    <Chip
                      style={{ backgroundColor: "#rgba(206, 206, 206, 0)" }}
                      label={this.state.notearr.reminder}
                      onDelete={() => this.handleDelete(this.state.notearr.id)}
                      variant="outlined"
                    />
                  </div>

                  {/* ):(null)
                 } */}

                  <div className="footerarea">
                    <div
                      style={{
                        justifyContent: "space-around",
                        display: "flex",
                        paddingTop: "13px",
                        paddingBottom: "13px"
                      }}
                    >
                      <Tooltip title="Remind me">
                        <AddAlertOutlinedIcon />
                      </Tooltip>
                      <Tooltip title="Collaborator">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          width="24"
                          height="24"
                          viewBox="0 0 24 24"
                          fill="#000"
                        >
                          <path d="M9 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0-6c1.1 0 2 .9 2 2s-.9 2-2 2-2-.9-2-2 .9-2 2-2zm0 7c-2.67 0-8 1.34-8 4v3h16v-3c0-2.66-5.33-4-8-4zm6 5H3v-.99C3.2 16.29 6.3 15 9 15s5.8 1.29 6 2v1zm3-4v-3h-3V9h3V6h2v3h3v2h-3v3h-2z" />
                        </svg>
                      </Tooltip>
                      <Tooltip title=" Change color">
                        <PaletteOutlinedIcon />
                      </Tooltip>
                      <AddArchive propsarchivenote={this.state.notearr.id} />
                      <Tooltip title=" More">
                        <Showmoreoption
                          propsdisplaynote={this.state.notearr.id}
                        />
                      </Tooltip>
                      <Button onClick={this.handleClose}>close</Button>
                    </div>
                  </div>
                </Card>
              </div>
            ) : null}
          </Dialog>
        </div>
      </div>
    );
  }
}

export default Displaynotes;
