import {TOGGLE_VIEW,} from '../Redux/actionType'

export const toggleView = () =>{
    return{
        type:TOGGLE_VIEW
    }
}


export const label_id=(labelid)=>{
    return{
        type:ADD_REMINDER,
        payload:labelid
    }
}
