import React from "react";
import PropTypes from "prop-types";
import Popover from "@material-ui/core/Popover";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";
import {
  Card,
  Tooltip,
  DialogContent,
  Button,
  IconButton
} from "@material-ui/core";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";

import { typography } from "@material-ui/system";

const styles = theme => ({
  popover: {
    pointerEvents: "none"
  },
  paper: {
    padding: theme.spacing.unit
  },
  button: {
    margin: theme.spacing.unit
  },
  input: {
    display: "none"
  }
});

const colorsPallete = [
  {
    colorName: "White",
    colorCode: "#ffffff"
  },
  {
    colorName: "Red",
    colorCode: "#ea2e2e"
  },
  {
    colorName: "Orange",
    colorCode: "#ffb600"
  },
  {
    colorName: "Yellow",
    colorCode: "#e1e82e"
  },
  {
    colorName: "Green",
    colorCode: "#ccff90"
  },
  {
    colorName: "Teal",
    colorCode: "#a7ffeb"
  },
  {
    colorName: "Blue",
    colorCode: "#281bd6"
  },
  {
    colorName: "Dark blue",
    colorCode: "#aecbfa"
  },
  {
    colorName: "Purple",
    colorCode: "#d7adfb"
  },
  {
    colorName: "Pink",
    colorCode: "#fdcfe8"
  },
  {
    colorName: "Dark Brown",
    colorCode: "#e6c9a7"
  },
  {
    colorName: "Gray",
    colorCode: "#e8eaed"
  }
];



class MouseOverPopover extends React.Component {
  state = {
    anchorEl: null
  };

  handlePopoverOpen = event => {
    this.setState({ anchorEl: event.currentTarget });
    event.preventDefault();
  };

  handlePopoverClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <Typography
        >
          <Tooltip title=" Change color">
            <PaletteOutlinedIcon onMouseEnter={this.handlePopoverOpen}
           />
          </Tooltip>
        </Typography>

        <Popover
          style={{ top: "-16%" ,z}}
          id="mouse-over-popover"
          className={classes.popover}
          classes={{
            paper: classes.paper
          }}
          open={open}
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: "top",
            horizontal: "left"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "left"
          }}

        >
          <div
            style={{
              position: "relative",
              width: "120px",
              margin: "auto",
              display: "flex",
              flexWrap: "wrap",
              flexDirection: "row"
            }}
          >
            {colorsPallete.map(usercolor => (
              <div style={{ padding: "3px" }}>
                <IconButton   
                   onClick={this.handleColorChange}
                  style={{ backgroundColor: usercolor.colorName }}
                ></IconButton>
              </div>
            ))}
          </div>
        </Popover>
      </div>
    );
  }
}

MouseOverPopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(MouseOverPopover);
