import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Popover from "@material-ui/core/Popover";
import "../Cssfile/addReminder.css";
import AccessTimeIcon from "@material-ui/icons/AccessTime";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";

import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker
} from "@material-ui/pickers";
import DateFnsUtils from "@date-io/date-fns";
import { addReminder } from "./Service";

const theme = createMuiTheme({
  overrides: {
    MuiPopover: {
      paper: {
        width: "16%"
      }
    }
    // ,
    // typography: {
    //   margin: theme.spacing.unit * 2
    // }
  }
});
class SimplePopover extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedReminder: new date(),
      currentDate: new Date()
    };
  }

  state = {
    anchorEl: null
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget
    });
  };

  addReminder = () =>{
    let noteid = this.props.propsnoteid;
    let token = localStorage.getItem("Token");

    addReminder()
  }

  handleClose = () => {


    let currentDate = this.state.currentDate.toLocaleDateString();
    let selectedDate = this.state.selectedReminder.toLocaleDateString();

    let currentTime = this.state.currentDate.getTime();
    let selectedTime = this.state.selectedReminder.getTime();

    if (currentDate.localeCompare(selectedDate) === 0) {
      if (currentTime < selectedTime) {
       this.addReminder();
      }
    }
    if (currentDate.localeCompare(selectedDate) < 0) {
      alert("2");
    }
    alert("Select correct time and date");

    this.setState({
      anchorEl: null
    });
  };

  handleDateChange = date => {
    let newDate = new Date(date);
    this.setState(date);
    console.log("userdate" + newDate.toLocaleDateString());
  };

  render() {
    const { selectedDate } = this.state;

    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <div onClick={this.handleClick} className="pickdatetime">
          <AccessTimeIcon fontSize="inherit" style={{ position: "fixed" }} />
          <span style={{ marginLeft: "32px" }}> Pick date & time</span>
        </div>
        <Popover
          style={{ top: "-150px", borderRadius: "4%", width: "18%" }}
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "center"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "center"
          }}
        >
          <div className="main">
            <ArrowBackIcon style={{ position: "fixed", fontSize: "22px" }} />
            <span style={{ marginLeft: "32px" }}> Pick date & time</span>
          </div>

          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              flexDirection: "column",
              padding: "4%"
            }}
          >
            <MuiPickersUtilsProvider utils={DateFnsUtils}>
              <MuiThemeProvider theme={theme}>
                <KeyboardDatePicker
                  margin="normal"
                  id="date-picker"
                  value={this.state.selectedReminder}
                  onChange={this.handleDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date"
                  }}
                />

                <KeyboardTimePicker
                  margin="normal"
                  id="time-picker"
                  value={this.state.selectedReminder}
                  onChange={this.handleDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change time"
                  }}
                  style={{ fontSize: "60%" }}
                />
              </MuiThemeProvider>
            </MuiPickersUtilsProvider>
          </div>

          <div
            onClick={this.handleClose}
            style={{
              display: "flex",
              justifyContent: "flex-end",
              padding: "4%"
            }}
          >
            Save
          </div>
        </Popover>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default SimplePopover;
