import React, { Component } from "react";
import { Card, Tooltip } from "@material-ui/core";
import "../Cssfile/Displaynotes.css";
import ImageOutlinedIcon from "@material-ui/icons/ImageOutlined";
import CheckBoxOutlinedIcon from "@material-ui/icons/CheckBoxOutlined";
import CreateOutlinedIcon from "@material-ui/icons/CreateOutlined";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import ArchiveOutlinedIcon from "@material-ui/icons/ArchiveOutlined";
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import ReplayOutlinedIcon from "@material-ui/icons/ReplayOutlined";
import RedoOutlinedIcon from "@material-ui/icons/RedoOutlined";
import { DisplayAllNotes } from "./Service";
import Showmoreoption from "./Showmoreoption";
import AddArchive from "./AddArchive";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import RestoreFromTrashIcon from "@material-ui/icons/RestoreFromTrash";

export class TrashNote extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      more: false,
      allNote: []
    };
  }

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {

        let array = [];
        Response.data.data.forEach(element => {
          if (element.trash === true) {

            array.push(element);
          }
        });
        this.setState({
          allNote: array
        });
      })
      .catch(err => {
        console.log("error");
      });
  };

  UNSAFE_componentWillMount() {
    this.getAllNotes();
  }
  handledisplaynote = () => {
    this.setState({ open: true });
  };
  handleclosenote = () => {
    this.setState({ open: false });
  };

  render() {
    return (
      <div
        style={{ paddingTop: "141px" }}
        onMouseLeave={this.handleclosenote}
        onMouseEnter={this.handledisplaynote}
      >
        {this.state.allNote.map(note =>
          !this.state.open ? (
            <div className="notedis" style={{ padding: 5 }}>
              <Card
                style={{
                  width: "222px",
                  boxShadow:
                    " 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)",
                  borderRadius: "11px"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "12px"
                  }}
                >
                  <b>{note.title}</b>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    top: "3%",
                    padding: "12px",
                    paddingTop: "11px",
                    paddingBottom: "45px"
                  }}
                >
                  {note.description}{" "}
                </div>
              </Card>
            </div>
          ) : (
            <div style={{ padding: 5 }}>
              <Card
                style={{
                  width: "222px",
                  boxShadow:
                    " 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)",
                  borderRadius: "11px"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "12px"
                  }}
                >
                  <div>
                    {" "}
                    <b>{note.title}</b>
                  </div>
                  <div></div>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    top: "3%",
                    padding: "12px",
                    paddingTop: "11px"
                  }}
                >
                  {note.description}
                </div>

                <div>
                  <div
                    style={{
                      justifyContent: "space-start",
                      display: "flex",
                      padding: "12px",
                      paddingBottom: "13px"
                    }}
                  >
                    <Tooltip title="delate Forever">
                      <DeleteForeverIcon />
                    </Tooltip>

                    <Tooltip title="Restore">
                      <RestoreFromTrashIcon style={{ paddingLeft: "22px" }} />
                    </Tooltip>
                  </div>
                </div>
              </Card>
            </div>
          )
        )}
      </div>
    );
  }
}

export default TrashNote;
