import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import ListItemText from "@material-ui/core/ListItemText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Dialog from "@material-ui/core/Dialog";
import PersonIcon from "@material-ui/icons/Person";
import AddIcon from "@material-ui/icons/Add";
import Typography from "@material-ui/core/Typography";
import blue from "@material-ui/core/colors/blue";
import ImageOutlinedIcon from "@material-ui/icons/ImageOutlined";
import CheckBoxOutlinedIcon from "@material-ui/icons/CheckBoxOutlined";
import CreateOutlinedIcon from "@material-ui/icons/CreateOutlined";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import ArchiveOutlinedIcon from "@material-ui/icons/ArchiveOutlined";
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import ReplayOutlinedIcon from "@material-ui/icons/ReplayOutlined";
import RedoOutlinedIcon from "@material-ui/icons/RedoOutlined";
import { Card, TextField } from "@material-ui/core";
import Showmoreoption from "./Showmoreoption";
import AddArchive from "./AddArchive";
import { editNote } from "./Service";

const styles = {
  avatar: {
    backgroundColor: blue[100],
    color: blue[600]
  }
};

class SimpleDialog extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      title: "",
      description: ""
    };
  }



  handleClose = () => {
    this.props.onClose(this.props.selectedValue);
  };

  handleListItemClick = value => {
    this.props.onClose(value);
    let noteid = this.props.noterestoretrash;
    console.log("noteddddddddd"+noteid);

  };

  handleEdittitle = event => {
    this.setState({
      title: event.target.value
    });
  };

  handleEditDescription = event => {
    this.setState({
      description: event.target.value
    });
  };
  handleEditnoteChange = () => {

    let editnote = {};

    editnote.title = this.state.title;
    editnote.description = this.state.description;
    let token = localStorage.getItem("Token");
    let noteid = this.props.propsarchivenote;

    editNote(editnote, noteid, token)
      .then(Response => {
        console.log("note  Edit success..");
      })
      .catch(err => {
        console.log("note not Edit success.."+err);
      });
  };

  render() {


    const { classes, onClose, selectedValue, ...other } = this.props;

    return (
      <Dialog
        onClose={this.handleClose}
        aria-labelledby="simple-dialog-title"
        {...other}
        onClick={this.handleEditnoteChange}
      >
        <div style={{ padding: 5 }}>
          <Card>
            <div
              style={{
                justifyContent: "space-between",
                display: "flex",
                paddingLeft: "13px",
                paddingRight: "13px",
                paddingTop: "13px"
              }}
            >
              <TextField
                color="white"
                name="notetitle"
                style={{ wordBreak: "break-all", wordWrap: "break-all" }}
                onChange={this.handleEdittitle}
              />
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
              >
                <path fill="none" d="M0 0h24v24H0z" />
                <path
                  fill="#000"
                  d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                />
              </svg>
            </div>

            <div
              style={{
                justifyContent: "space-between",
                display: "flex",
                paddingLeft: "13px",
                paddingTop: "13px",
                paddingBottom: "13px"
              }}
            >
              <TextField
                color="white"
                name="takeanote"
                onChange={this.handleEditDescription}
              />
            </div>

            <div
              style={{
                justifyContent: "space-around",
                display: "flex",
                paddingBottom: "13px"
              }}
            >
              {" "}
              <AddAlertOutlinedIcon />
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="#000"
              >
                <path d="M9 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0-6c1.1 0 2 .9 2 2s-.9 2-2 2-2-.9-2-2 .9-2 2-2zm0 7c-2.67 0-8 1.34-8 4v3h16v-3c0-2.66-5.33-4-8-4zm6 5H3v-.99C3.2 16.29 6.3 15 9 15s5.8 1.29 6 2v1zm3-4v-3h-3V9h3V6h2v3h3v2h-3v3h-2z" />
              </svg>
              <PaletteOutlinedIcon />
              <ImageOutlinedIcon />
              <AddArchive propsdisplaynote={this.props.id} />
              <MoreVertOutlinedIcon />
              <b onClick={this.handleOnClickAwayChange}>close</b>
            </div>
          </Card>
        </div>
      </Dialog>
    );
  }
}

SimpleDialog.propTypes = {
  classes: PropTypes.object.isRequired,
  onClose: PropTypes.func,
  selectedValue: PropTypes.string
};

const SimpleDialogWrapped = withStyles(styles)(SimpleDialog);

class SimpleDialogDemo extends React.Component {
  state = {
    open: false
  };

  handleClickOpen = () => {
    this.setState({
      open: true
    });
  };

  handleClose = value => {
    this.setState({ selectedValue: value, open: false });
  };

  render() {
    return (
      <div>
        <Button
          variant="outlined"
          color="primary"
          onClick={this.handleClickOpen}
        >
          Open simple dialog
        </Button>
        <SimpleDialogWrapped
          selectedValue={this.state.selectedValue}
          open={this.state.open}
          onClose={this.handleClose}
        />
      </div>
    );
  }
}

export default SimpleDialogDemo;
