import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import ListItemText from "@material-ui/core/ListItemText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Dialog from "@material-ui/core/Dialog";
import PersonIcon from "@material-ui/icons/Person";
import AddIcon from "@material-ui/icons/Add";
import Typography from "@material-ui/core/Typography";
import blue from "@material-ui/core/colors/blue";
import { Divider, TextField, InputBase } from "@material-ui/core";
import PersonAddIcon from "@material-ui/icons/PersonAdd";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import DoneOutlinedIcon from "@material-ui/icons/DoneOutlined";
import { Card, Tooltip } from "@material-ui/core";
import { addCollabrator } from "./Service";

const styles = {
  avatar: {
    backgroundColor: blue[100],
    color: blue[600]
  }
};

const theme = createMuiTheme({
  overrides: {
    MuiInputBase: {
      input: {
        width: "513px",
        height: "1.875em",
        paddingLeft: "8px"
      }
    },
    MuiAvatar: {
      img: {
        width: "43%",
        borderRadius: "50%",
        height: "44%"
      }
    }
  }
});

class SimpleDialog extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      done: false,
      collaboratorData: ""
    };
  }
  doneClickhandle = event => {
    this.setState({
      collaboratorData: event.target.value
    });
    console.log(this.state.collaboratorData);

    this.setState({ done: true });
  };
  handleClose = () => {
    this.props.onClose(this.props.selectedValue);
  };
  donenotClickhandle = () => {
    let collaboratorinfo = {};
    collaboratorinfo.noteid = this.props.propsRemindernote;
    collaboratorinfo.colaboratorId = this.state.collaboratorData;
    let token = localStorage.getItem("Token");

    console.log("a" + this.props.propsRemindernote);

    addCollabrator(collaboratorinfo, token)
      .then(Response => {
        console.log("add collaborator");
      })
      .catch(err => {
        console.log("not add collaborator");
      });

    this.setState({ done: false });
  };
  handleListItemClick = value => {
    this.props.onClose(value);
  };

  handleClickClose = () => {
    this.setState({
      open: false
    });
  };
  render() {
    let name1 = localStorage.getItem("Name");
    let email = localStorage.getItem("Email");
    const { classes, onClose, selectedValue, ...other } = this.props;

    return (
      <Dialog
        onClose={this.handleClose}
        aria-labelledby="simple-dialog-title"
        {...other}
      >
        <MuiThemeProvider theme={theme}>
          <div
            style={{
              display: "flex",
              flexWrap: "wrap",
              width: "600px",
              flexDirection: "column"
            }}
          >
            <span
              style={{
                padding: "2%",
                fontSize: "large",
                fontFamily: "inherit",
                fontStyle: "unset"
              }}
            >
              <b>Collaborators</b>
            </span>
            <span style={{ padding: "2%" }}>
              {" "}
              <Divider />
            </span>
            <div    style={{
                      display: "flex",
                      justifyContent: "space-between"
                    }}>
              <span>
                <Avatar
                  alt={localStorage.getItem("Name")}
                  src={localStorage.getItem("ProfilePic")}
                  style={{ width: "77px", height: "77px" }}
                />
              </span>
              <div>
              <span>
                <b>{name1}</b>(Owner)
              </span>
              <span>{email}</span>
              </div>
            </div>
            <span style={{ padding: "1%" }}>
              <Avatar></Avatar>
            </span>
            <div
              style={{
                display: "flex",
                justifyContent: "flex-start",
                flexDirection: "row",
                padding: "1%"
              }}
            >
              <span>
                <Avatar>
                  <PersonAddIcon />
                </Avatar>
              </span>
              <span>
                <InputBase
                  width="600px"
                  id="standard-basic"
                  placeholder="Person or email to share with"
                  onChange={this.doneClickhandle}
                />
              </span>
              {this.state.done !== false ? (
                <span>
                  <DoneOutlinedIcon onClick={this.donenotClickhandle} />
                </span>
              ) : null}
            </div>
            <div
              style={{
                display: "flex",
                justifyContent: "flex-end ",
                flexDirection: "row",
                padding: " 3%"
              }}
            >
              <span
                style={{ marginRight: "56px" }}
                onClick={this.handleClickClose}
              >
                Cancel
              </span>
              <span>Save</span>
            </div>
          </div>
        </MuiThemeProvider>
      </Dialog>
    );
  }
}

SimpleDialog.propTypes = {
  classes: PropTypes.object.isRequired,
  onClose: PropTypes.func,
  selectedValue: PropTypes.string
};

const SimpleDialogWrapped = withStyles(styles)(SimpleDialog);

class SimpleDialogDemo extends React.Component {
  state = {
    open: false
  };

  handleClickOpen = () => {
    this.setState({
      open: true
    });
  };

  handleClose = value => {
    this.setState({ selectedValue: value, open: false });
  };

  render() {
    return (
      <div>
        <Tooltip title="Collaborator" onClick={this.handleClickOpen}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="#000"
          >
            <path d="M9 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0-6c1.1 0 2 .9 2 2s-.9 2-2 2-2-.9-2-2 .9-2 2-2zm0 7c-2.67 0-8 1.34-8 4v3h16v-3c0-2.66-5.33-4-8-4zm6 5H3v-.99C3.2 16.29 6.3 15 9 15s5.8 1.29 6 2v1zm3-4v-3h-3V9h3V6h2v3h3v2h-3v3h-2z" />
          </svg>
        </Tooltip>
        <SimpleDialogWrapped
          selectedValue={this.state.selectedValue}
          open={this.state.open}
          onClose={this.handleClose}
        />
      </div>
    );
  }
}

export default SimpleDialogDemo;
