import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Popover from "@material-ui/core/Popover";
import "../Cssfile/addReminder.css";
import AccessTimeIcon from "@material-ui/icons/AccessTime";
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import Grid from '@material-ui/core/Grid';
import { MuiPickersUtilsProvider, TimePicker, DatePicker } from 'material-ui-pickers';
const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2
  }
});

class SimplePopover extends React.Component {
  handleDateChange = date => {
    this.setState({ selectedDate: date });
  };

  state = {
    anchorEl: null
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget
    });
  };

  handleClose = () => {
    this.setState({
      anchorEl: null
    });
  };

  render() {
    const { classes1} = this.props;
    const { selectedDate } = this.state;
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <div onClick={this.handleClick} className="pickdatetime">
          <AccessTimeIcon fontSize="inherit" style={{ position: "fixed" }} />
          <span style={{ marginLeft: "32px" }}> Pick date & time</span>
        </div>
        <Popover
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "center"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "center"
          }}
        >
          <div className="main">
          <ArrowBackIcon  style={{ position: "fixed",fontSize:"22px" }}/>
          <span style={{ marginLeft: "32px" }}> Pick date & time</span>

          </div>
          <div>


        <MuiPickersUtilsProvider>
        <Grid container className={classes.grid} justify="space-around">
          <DatePicker
            margin="normal"
            label="Date picker"
            value={selectedDate}
            onChange={this.handleDateChange}
          />
          <TimePicker
            margin="normal"
            label="Time picker"
            value={selectedDate}
            onChange={this.handleDateChange}
          />
        </Grid>
      </MuiPickersUtilsProvider>    

          </div>
        </Popover>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(SimplePopover);
