import React, { Component } from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Popover from '@material-ui/core/Popover';
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";

const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2,
  },
});

class SimplePopover extends React.Component {
  state = {
    anchorEl: null,
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget,
    });
  };

  handleClose = () => {
    this.setState({
      anchorEl: null,
    });
  };

  handleDeletenoteChange=()=>{

    let token = localStorage.getItem("Token");
    let note= props.propsdisplaynote.state




  }
  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
          <MoreVertOutlinedIcon
              onClick={this.handleClick}
          />


        <Popover style={{top:'18px',left:'40px',width:'150px'}}
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'center',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'center',
          }}
        >
          <Typography className={classes.typography}  style={{display:'flex',justifyContent:'start',flexDirection:'column'}}>


  <Button  onClick={this.handleDeletenoteChange}>Delete note</Button>
  <Button>Add label</Button>
  <Button>Add drawing</Button>
  <Button>show checkboxes </Button>


</Typography>
        </Popover>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimplePopover);
