import React, { Component } from "react";
import { createNote, DisplayAllNotes } from "./Service";
import Dashboard from "./Dashboard";
import CreateNote from "./CreateNote";
import Displaynotes from "./Displaynotes";
import { typography } from "@material-ui/system";

export class ShowNote extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      more: false,
      allNote: []
    };
  }

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {
        this.setState({
          allNote: Response.data.data
        });
      })
      .catch(err => {
        console.log("error");
      });
  };
  UNSAFE_componentWillMount() {
    this.getAllNotes();
  }

  render() {
    return (
      <div>
        <CreateNote />

    {/* padding: 5px;
    padding-top: 106px;     */}
    <div style={{display:'flex',width:'800px',flexWrap:'wrap',flexDirection:'row' }}>
        {this.state.allNote.map((note, index) => (

          <Displaynotes
            title={this.state.allNote[index].title}
            description={this.state.allNote[index].description}
            id={this.state.allNote[index].id}
          />

        ))}
         </div>
      </div>
    );
  }
}

export default ShowNote;
