import React, { Component } from "react";
import { Paper, InputBase, Card, ClickAwayListener, Button, TextField } from "@material-ui/core";
import "../Cssfile/CreateNote.css";
import ImageOutlinedIcon from "@material-ui/icons/ImageOutlined";
import CheckBoxOutlinedIcon from "@material-ui/icons/CheckBoxOutlined";
import CreateOutlinedIcon from "@material-ui/icons/CreateOutlined";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import { createNote } from "./Service";
import AddArchive from "./AddArchive";
import {  Tooltip } from "@material-ui/core";


export class CreateNote extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      title: "",
      description: ""
    };
  }









  handleOpenNoteChange = () => {
    this.setState({ open: true });
  };
  handleCloseChange = () => {
    this.setState({ open: false });
  };
  handlechangetitle = event => {
    this.setState({
      title: event.target.value
    });
    console.log(this.state.title);
  };
  handlechangedescription = event => {
    this.setState({
      description: event.target.value
    });
    console.log(this.state.description);
  };


  handleOnClickAwayChange = () => {
    this.setState({ open: false });
    let note = {};

    if (note !== null) {
      note.title = this.state.title;
      note.description = this.state.description;
      let token = localStorage.getItem("Token");

      createNote(note, token)
        .then(Response => {
          console.log("note  created..");
          this.props.refresh()
        })
        .catch(err => {
          console.log("note not created..");
        });
    }
  };



  render() {
    return (
      <ClickAwayListener onClickAway={this.handleOnClickAwayChange}>
        <div className="createnote">
          <Paper
           style={{width:'50%',position:'relative',margin:'auto'}}
          >
            {!this.state.open ? (
              <Card>
                <div
                  style={{
                    justifyContent: "space-around",
                    display: "flex",
                    paddingTop: "6px",
                    paddingBottom: "6px"
                  }}
                >
                  <InputBase
                    color="white"
                    placeholder="Take a note..."
                    onClick={
                      this.state.open
                        ? this.handleCloseChange
                        : this.handleOpenNoteChange
                    }
                  />

                  <CheckBoxOutlinedIcon />
                  <CreateOutlinedIcon />
                  <ImageOutlinedIcon />
                </div>
              </Card>
            ) : (
              <Card>
                <div
                  style={{
                    justifyContent: "space-between",
                    display: "flex",
                    paddingLeft: "13px",
                    paddingRight: "13px",
                    paddingTop: "13px"
                  }}
                >

                          <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    color="white"
                    placeholder="Title"
                    name="notetitle"
                    multiline
                    fullWidth
                    margin="normal"
                    InputProps={{
                      disableUnderline: true
                    }}
                    onChange={this.handlechangetitle}

                  />

                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                  >
                    <path fill="none" d="M0 0h24v24H0z" />
                    <path
                      fill="#000"
                      d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                    />
                  </svg>
                </div>

                <div
                  style={{
                    justifyContent: "space-between",
                    display: "flex",
                    paddingLeft: "13px",
                    paddingTop: "13px",
                    paddingBottom: "13px"
                  }}
                >
                  <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    color="white"
                    placeholder="Title"
                    name="notetitle"
                    multiline
                    fullWidth
                    margin="normal"
                    InputProps={{
                      disableUnderline: true
                    }}
                    onChange={this.handlechangetitle}

                  />
                  <InputBase
                    color="white"
                    placeholder="Take a Note..."
                    name="takeanote"
                    onChange={this.handlechangedescription}
                  />
                </div>

                <div
                  style={{
                    justifyContent: "space-around",
                    display: "flex",
                    paddingBottom: "13px"
                  }}
                >
                  {" "}
                  <AddAlertOutlinedIcon />
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="#000"
                  >
                    <path d="M9 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0-6c1.1 0 2 .9 2 2s-.9 2-2 2-2-.9-2-2 .9-2 2-2zm0 7c-2.67 0-8 1.34-8 4v3h16v-3c0-2.66-5.33-4-8-4zm6 5H3v-.99C3.2 16.29 6.3 15 9 15s5.8 1.29 6 2v1zm3-4v-3h-3V9h3V6h2v3h3v2h-3v3h-2z" />
                  </svg>
                  <PaletteOutlinedIcon />
                  <ImageOutlinedIcon />
                  <AddArchive  propsdisplaynote={this.props.id} refresh={this.props.refresh()}/>

                  <Tooltip title="Remind me">
                    <AddAlertOutlinedIcon />
                  </Tooltip>
                  <Button onClick={this.handleOnClickAwayChange}><b>close</b></Button>
                </div>
              </Card>
            )}
          </Paper>


        </div>




      </ClickAwayListener>
    );
  }
}

export default CreateNote;
