import React, { Component } from "react";
import "../Cssfile/Register.css";
import TextField from "@material-ui/core/TextField";
import IconButton from "@material-ui/core/IconButton";
import Input from "@material-ui/core/Input";
import FilledInput from "@material-ui/core/FilledInput";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import Button from "@material-ui/core/Button";
import fundoo from "../image/account.svg";
import { UserRegistration } from "./Service";

export class Register extends Component {
  alreadyregister = () => {
    this.props.history.push("/");
  };
  handelRegister = () =>{
    if(this.state.password!=this.state.confPassword){
      alert('both passwords should be same')
    }else{
      let user ={}
      user.firstname = this.state.firstName
      user.lastname = this.state.lastName
      user.email = this.state.email
      user.password = this.state.password
      user.phonenumber = this.state.phoneNumber
      UserRegistration(user).then(res=>{
        log(res,'user registered success')
        alert('registration success')
      }).catch(err=>{
        log(err,"registration fail")
      })
    }
  }

  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };

  handleClickShowPassword = () => {
    this.setState(state => ({ showPassword: !state.showPassword }));
  };

  constructor(props) {
    super(props);

    this.state = {
      showPassword: false,
      email: "",
      password: "",
      firstName: "",
      lastName: "",
      phoneNumber: "",
      confPassword: ""
    };
  }
  handelFirstNameChange=(event)=>{
    this.setState({
      firstName:event.target.value
    })
    console.log(this.state.firstName);    
  }
  handleLastNameChange



  render() {
    return (
      <div className="userregister">
        <div className="userfundoo">
          <span style={{ color: "blue" }}>f</span>
          <span style={{ color: "red" }}>u</span>
          <span style={{ color: "orange" }}>n</span>
          <span style={{ color: "blue" }}>d</span>
          <span style={{ color: "green" }}>o</span>
          <span style={{ color: "red" }}>o</span>
        </div>
        <div className="userSignUp">SignUp</div>
        <div className="main">
          <div>
            <div className="userfirstlastname">
              <TextField
                margin="dense"
                size="small"
                required
                id="outlined"
                label="First Name"
                variant="outlined"
                style={{ width: "44%" }}
                onChange={this.handelFirstNameChange}
              />
              <TextField
                margin="dense"
                size="small"
                required
                id="outlined"
                label="Last Name"
                variant="outlined"
                style={{ width: "44%" }}
                onChange={this.handleLastNameChange}
              />
            </div>
            <div className="useremail1">
              <TextField
                margin="dense"
                size="small"
                required
                id="outlined"
                label="Email"
                variant="outlined"
              />
            </div>
            <div className="phoneNumber">
              <TextField
                size="small"
                className="phoneNumber"
                required
                id="outlined"
                label="Phone Number"
                variant="outlined"
                margin="dense"
              />
            </div>

            <div className="userPassword">
              <TextField
                size="small"
                id="outlined-adornment-password"
                variant="outlined"
                type={this.state.showPassword ? "text" : "password"}
                label="password"
                margin="dense"
                InputProps={{
                  endAdornment: (
                    <InputAdornment
                      position="end"
                      sytle={{ width: "1px" }}
                    ></InputAdornment>
                  )
                }}
                style={{ width: "44%" }}
              />
              <TextField
                size="small"
                margin="dense"
                id="outlined-adornment-password"
                variant="outlined"
                type={this.state.showPassword ? "text" : "password"}
                label=" confirm "
                value={this.state.password}
                onChange={this.handleChange("password")}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end" sytle={{ width: "1px" }}>
                      <IconButton
                        aria-label="Toggle password visibility"
                        onClick={this.handleClickShowPassword}
                      >
                        {this.state.showPassword ? (
                          <VisibilityOff />
                        ) : (
                          <Visibility />
                        )}
                      </IconButton>
                    </InputAdornment>
                  )
                }}
                style={{ width: "44%" }}
              />
            </div>

            <div className="userbutton">
              <Button
                margin="dense"
                size="small"
                variant="contained"
                color="primary"
                onClick={this.handelRegister}
              >
                Submit
              </Button>
              <Button onClick={this.alreadyregister}>
                Already Registered?
              </Button>
            </div>
          </div>

          <div className="img1">
            <img src={fundoo} width="80%" height="60%" alt="hello"></img>
            <p style={{ alignItems: "center" }}>
              One account.All of Fundoo working for you
            </p>
          </div>
        </div>
      </div>
    );
  }
}

export default Register;
