import React, { Component } from "react";
import "../Cssfile/Dashboard.css";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import InputBase from "@material-ui/core/InputBase";
import keeplogo from "../image/keeplogo.png";
import keep1 from "../image/1.svg";
import SearchIcon from "@material-ui/icons/Search";
import SettingsOutlinedIcon from "@material-ui/icons/SettingsOutlined";
import RefreshOutlinedIcon from "@material-ui/icons/RefreshOutlined";
import Profile from "../component/Profile";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import MyDrawer from "./MyDrawer";
import TextField from "@material-ui/core/TextField";
import { searchNote } from "./Service";

const theme = createMuiTheme({
  overrides: {

    MuiDrawer: {
      paper: {
        top: "13%"
      }
    },

    PersistentDrawerLeft: {
      drawer: {
        width: "0px"
      }
    }
  }
});

class Dashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      anchorEl: null,
      userprofle: false,
      allNote: [],
      SearchData:""
    };
  }




  handleshowprofile = () => {
    this.setState({ userprofle: true });
  };

  handlehideprofile = () => {
    this.setState({ userprofle: false });
  };


  handleAllReminderNote=()=>{
    console.log('reminder method call');
    this.props.history.push('/Dashboard/reminder');
  }
   handleAllTrashnote=()=>{
     this.props.history.push('/dashboard/trash')
   }

   handleshowAllNotenote=()=>{
    this.props.history.push('/dashboard/note')
  }
  handlesAllArchivenote=()=>{
    this.props.history.push('/dashboard/archive')
  }

  handleSearchDataChange=event=>{
    this.setState({
      SearchData:event.target.value
    })
    console.log(this. state.SearchData);


  }

  UNSAFE_componentWillMount() {
    this.getAllSearchNotes();
  }
  getAllSearchNotes = () => {
    let token = localStorage.getItem("Token");

    searchNote(this.state.SearchData)
      .then(Response => {
        console.log(Response.data.data);
        let array = [];
        // Response.data.data.forEach(element => {
        //   if (element.trash === false && element.archive === false) {
        //     console.log(element);
        //     array.push(element);
        //   }
        // });
        // this.setState({
        //   allNote: array
        // });
      })
      .catch(err => {
        console.log("error");
      });
  };




  render() {
    console.log(this.props,"dashboeard props");
    console.log(this.state.allNote);

    return (
      <div className="mainDashboard">
        <MuiThemeProvider theme={theme}>
          <AppBar position="fixed">
            <Toolbar className="toolbar" style={{ backgroundColor: "white" }}>
              <div className="keepAndLogo">
                <div>
                  <MyDrawer showNoteclick={this.handleshowAllNotenote}  trashclick={this.handleAllTrashnote} reminderClick={this.handleAllReminderNote} archiveclick={this.handlesAllArchivenote}/>

                </div>
                <img
                  src={keeplogo}
                  width="50px"
                  height="50px"
                  alt="hello"
                ></img>
                <Typography
                  className="keep"
                  variant="h6"
                  color="inherit"
                  noWrap
                >
                  <b style={{ marginLeft: "2%" }}>Fundoo</b>
                </Typography>
              </div>

              <div className="dashboardSearch">
                <div className="dashboardsearchicon">
                  <SearchIcon />
                </div>
                <div style={{ width: "90%" }}>
                  <TextField
                    color="white"
                    placeholder="Search…"
                    onChange={this.handleSearchDataChange}
                    id="standard-full-width"
                    multiline
                    fullWidth
                    margin="normal"
                    InputProps={{
                      disableUnderline: true
                    }}
                    style={{
                      justifyContent: "start",
                      display: "flex",

                    }}

                  />
                </div>
              </div>

              <div className="logokeep1">
                <RefreshOutlinedIcon />
                <img src={keep1} width="22px" height="22px" alt="hello"></img>
                <SettingsOutlinedIcon />
              </div>

              <div>
                <Profile PropsDashboard={this.props} />
              </div>
            </Toolbar>
          </AppBar>
        </MuiThemeProvider>


      </div>
    );
  }
}

export default Dashboard;
