import React, { Component } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Popover from "@material-ui/core/Popover";
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import { addIntrash } from "./Service";

const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2
  }
});

class SimplePopover extends React.Component {
  constructor(props) {
    super(props);

    this.state = {};
  }
  state = {
    anchorEl: null
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget
    });
  };

  handleClose = () => {
    this.setState({
      anchorEl: null
    });
  };

  handleDeletenoteChange = () => {
    let token = localStorage.getItem("Token");
    let noteid = this.props.propsdisplaynote;

    addIntrash(noteid, token)
      .then(Response => {
        console.log("add in trash");
      })
      .catch(err => {
        console.log("error");
      });
  };

  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <MoreVertOutlinedIcon onClick={this.handleClick} />

        <Typography
          className={classes.typography}
          style={{
            display: "flex",
            justifyContent: "start",
            flexDirection: "column"
          }}
        >
          <h6 onClick={this.handleDeletenoteChange}>Delete note</h6>
          <h6>Add label</h6>
        </Typography>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(SimplePopover);
