import React, { Component } from "react";
import { Card, Tooltip } from "@material-ui/core";
import "../Cssfile/Displaynotes.css";
import { DisplayAllNotes } from "./Service";

import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import RestoreFromTrashIcon from "@material-ui/icons/RestoreFromTrash";
import Restoretrash from "./Restoretrash";
import DeleteNotepermanent from "./DeleteNotepermanent";

export class TrashNote extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      more: false,
      allNote: []
    };
  }

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {
        let array = [];
        Response.data.data.forEach(element => {
          if (element.trash === true) {

            array.push(element);
          }
        });
        this.setState({
          allNote: array
        });
        // this.props.refresh.
      })
      .catch(err => {
        console.log("error");
      });
  };

  UNSAFE_componentWillMount() {
    this.getAllNotes();
  }
  handledisplaynote = () => {
    this.setState({ open: true });
  };
  handleclosenote = () => {
    this.setState({ open: false });
  };

  render() {
    return (
      <div
        style={{
          width: "800px",
          position: "relative",
          margin: "auto",
          display: "flex",
          flexWrap: "wrap",
          flexDirection: "row",
          paddingTop: "105px",
          paddingLeft: "13%"
        }}
      >
        {this.state.allNote.map(note => (
          <div className="trashnoted" style={{ padding: 5 }}>
            <Card
              style={{
                width: "222px",
                boxShadow:
                  " 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)",
                borderRadius: "11px"
              }}
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  padding: "12px"
                }}
              >
                <div>
                  {" "}
                  <b>{note.title}</b>
                </div>
                <div></div>
              </div>
              <div
                style={{
                  justifyContent: "spacearound",
                  display: "flex",
                  top: "3%",
                  padding: "12px",
                  paddingTop: "11px"
                }}
              >
                {note.description}
              </div>

              <div className="footeraa">
                <div
                  style={{
                    justifyContent: "space-start",
                    display: "flex",
                    padding: "12px",
                    paddingBottom: "13px"
                  }}
                >
                  <DeleteNotepermanent  refresh={this.ge}/>

                  <Restoretrash />
                </div>
              </div>
            </Card>
          </div>
        ))}
      </div>
    );
  }
}

export default TrashNote;
