import React, { Component } from "react";
import {
  IconButton,
  Drawer,
  createMuiTheme,
  List,
  ListItem,
  ListItemIcon,
  Divider
} from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import { MuiThemeProvider } from "@material-ui/core/styles";
import ListItemText from "@material-ui/core/ListItemText";
import EmojiObjectsOutlinedIcon from '@material-ui/icons/EmojiObjectsOutlined';
import AddAlertOutlinedIcon from '@material-ui/icons/AddAlertOutlined';
import DeleteOutlineOutlinedIcon from '@material-ui/icons/DeleteOutlineOutlined';
import ArchiveOutlinedIcon from '@material-ui/icons/ArchiveOutlined';
const theme = createMuiTheme({
  overrides: {
    MuiDrawer: {
      paper: {
        marginTop: "70px",
        width: "20%"
      }
    }
  }
});

export class MyDrawer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      AnchorEl: null
    };
  }
  handleDrawer = event => {
    const { currentTarget } = event;
    this.setState({
      AnchorEl: currentTarget,
      open: !this.state.open
    });

    let currentState = {
      allNote: false,
      archiveNote: false,
      trashNote: false,
      label: false,
      name: null,
      labelId: 0,

    };
  };


  handleAllNoteschange=()=>{  

     let  currentState={
       reminder:false,
       allnote:true,
       archivenote:false,
       trashnote:false,
       label:false,
       name:null,
       labelid:0,




     }
  }
  handleAllReminderNote=()=>{
    let  currentState={
      reminder:true,
      allnote:false,
      archivenote:false,
      trashnote:false,
      label:false,
      name:null,
      labelid:0,
    }
    console.log('reminder method call');
    this.props.history.push('/Dashboard/reminder');
  }

  handleAllTrashNote=()=>{

    let  currentState={
      reminder:false,
      allnote:false,
      archivenote:false,
      trashnote:true,
      label:false,
      name:null,
      labelid:0,


    }
  }

  handleAllArchivehange=()=>{

    let  currentState={
      reminder:false,
      allnote:false,
      archivenote:true,
      trashnote:false,
      label:false,
      name:null,
      labelid:0,


    }
  }
  handleAllLabelhange=(name,labelid)=>{

    let  currentState={
      reminder:false,
      allnote:false,
      archivenote:false,
      trashnote:false,
      label:true,
      name:name,
      labelid:labelid,
    }
  }


  render() {
    let open = this.state.open;
    return (
      <div>
        <MuiThemeProvider theme={theme}>
          <IconButton onClick={this.handleDrawer}>
            <MenuIcon />
          </IconButton>
          <div style={{ textAlign: "initial" }}>
            <Drawer variant="persistent" anchor="left" open={open}>
              <List>

                <ListItem
                     onClick={()=>this.props.showNoteclick()}
                  button
                  key="Notes"
                  style={{
                    borderTopRightRadius: "10px",
                    borderBottomRightRadius: "10px"
                  }}
                >
                  <ListItemIcon>
                     <EmojiObjectsOutlinedIcon/>
                  </ListItemIcon>
                  <ListItemText primary="Notes" />
                </ListItem>

                <ListItem
                onClick={()=>this.props.reminderClick()}
                  button
                  key="Reminders"
                  style={{
                    borderTopRightRadius: "10px",
                    borderBottomRightRadius: "10px"
                  }}
                >
                  <ListItemIcon>
                   <AddAlertOutlinedIcon/>
                  </ListItemIcon>
                  <ListItemText primary="Reminder" />
                </ListItem>
              </List>
              <Divider></Divider>
              <div
                class="JNdkSc-tJHJj"
                style={{
                  paddingLeft: "10%",
                  paddingTop: "5%",
                  paddingBottom: "5%"
                }}
              >
                Labels
              </div>
              <List>
                <ListItem
                  onClick={this.handleAllLabelhange}
                  button
                  key="label"
                  style={{
                    borderTopRightRadius: "10px",
                    borderBottomRightRadius: "10px"
                  }}
                >
                  <ListItemIcon>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="24"
                      height="24"
                      viewBox="0 0 24 24"
                      className="gb_Tc"
                    >
                      <path d="M18 17v-6c0-3.07-1.63-5.64-4.5-6.32V4c0-.83-.67-1.5-1.5-1.5s-1.5.67-1.5 1.5v.68C7.64 5.36 6 7.92 6 11v6H4v2h16v-2h-2zm-2 0H8v-6c0-2.48 1.51-4.5 4-4.5s4 2.02 4 4.5v6zm-4 5c1.1 0 2-.9 2-2h-4c0 1.1.9 2 2 2z"></path>
                    </svg>
                  </ListItemIcon>
                  <ListItemText primary="Add Label" />
                </ListItem>
              </List>
              <Divider></Divider>
              <List>
                <ListItem

                    onClick={()=>this.handleAllArchivehange}

                  button
                  key="Archive"
                  style={{
                    borderTopRightRadius: "10px",
                    borderBottomRightRadius: "10px"
                  }}  
                >
                  <ListItemIcon>
                   <ArchiveOutlinedIcon/>
                  </ListItemIcon>
                  <ListItemText primary="Archive" />
                </ListItem>
                <ListItem

                  onClick={()=>this.props.trashclick()}

                  button
                  key="Trash"
                  style={{
                    borderTopRightRadius: "10px",
                    borderBottomRightRadius: "10px"
                  }}
                >
                  <ListItemIcon>
                   <DeleteOutlineOutlinedIcon/>
                  </ListItemIcon>
                  <ListItemText primary="Trash" />
                </ListItem>
              </List>
            </Drawer>
          </div>
        </MuiThemeProvider>
      </div>
    );
  }
}

export default MyDrawer;
