import React from "react";
import PropTypes from "prop-types";
import Popover from "@material-ui/core/Popover";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";
import { Card, Tooltip, DialogContent, Button } from "@material-ui/core";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import AddIcon from "@material-ui/icons/Add";

import Fab from "@material-ui/core/Fab";

const styles = theme => ({
  popover: {
    pointerEvents: "none"
  },
  paper: {
    padding: theme.spacing.unit
  },
  MuiFab:{root:{
    height: '30px'

  }}
});

class MouseOverPopover extends React.Component {
  state = {
    anchorEl: null
  };

  handlePopoverOpen = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handlePopoverClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <Typography
          aria-owns={open ? "mouse-over-popover" : undefined}
          aria-haspopup="true"
          onMouseEnter={this.handlePopoverOpen}
          onMouseLeave={this.handlePopoverClose}
        >
          <Tooltip title=" Change color">
            <PaletteOutlinedIcon />
          </Tooltip>
        </Typography>
        <Popover
          style={{ top: "-14%" }}
          id="mouse-over-popover"
          className={classes.popover}
          classes={{
            paper: classes.paper
          }}
          open={open}
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: "top",
            horizontal: "left"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "left"
          }}
          onClose={this.handlePopoverClose}
          disableRestoreFocus
        >
          <div  
            style={{
              position: "relative",
              margin: "auto",
              display: "flex",
              flexWrap: "wrap",
              flexDirection: "row",
            }}
          >

             <span>   <Fab color="primary" size="small"  style={{
                  display: "flex",
                  justifyContent: "space-between",
                  padding: "12px"
                }}></Fab ></span>
             <span>   <Fab color="primary" size="small"  style={{
                  display: "flex",
                  justifyContent: "space-between",
                  padding: "12px"
                }}></Fab></span>
             <span>   <Fab color="primary" size="small" style={{width: '30px',
    minheight: "30px"}}></Fab></span>
             <span>   <Fab color="primary"></Fab></span>
             <span>   <Fab color="primary" size="small"></Fab></span>
             <span>   <Fab color="primary" size="small"></Fab></span>
             <span>   <Fab color="primary" size="small"></Fab></span>
             <span>   <Fab color="primary" size="small"></Fab></span>
             <span>   <Fab color="primary" size="small"></Fab></span>
             <span>   <Fab color="primary" size="small"></Fab></span> 
             <span>   <Fab color="primary" size="small"></Fab></span>


          </div>
        </Popover>
      </div>
    );
  }
}

MouseOverPopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(MouseOverPopover);
