import React, { Component } from "react";
import { Card } from "@material-ui/core";
import "../Cssfile/TrashNote.css";
import { DisplayAllNotes } from "./Service";
import {Redirect} from 'react-router-dom'

import Restoretrash from "./Restoretrash";
import DeleteNotepermanent from "./DeleteNotepermanent";
import TextField from "@material-ui/core/TextField";
import {connect} from 'react-redux';

const mapStateToProps= state=>{
  return {
       view:state.view
  }
}

export class TrashNote extends Component {
  constructor(props) {
    super(props);
    let token = localStorage.getItem("Token");
    let loggedIn = true;
    if (token == null) {
      loggedIn = false;
    }
    this.state = {
      loggedIn,
      open: false,
      more: false,
      allNote: []
    };
  }

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {
        let array = [];
        Response.data.data.forEach(element => {
          if (
            element.trash === true ||
            (element.trash === true && element.archive === true)
          ) {
            array.push(element);
          }
        });
        this.setState({
          allNote: array
        });
      })
      .catch(err => {
        console.log("error");
      });
  };

  UNSAFE_componentWillMount() {
    this.getAllNotes();
  }
  handledisplaynote = () => {
    this.setState({ open: true });
  };
  handleclosenote = () => {
    this.setState({ open: false });
  };

  render() {
    if (this.state.loggedIn === false) {

      return <Redirect to="/login"/>  }
    return (
      <div
        style={{
          width: "800px",
          position: "relative",
          margin: "auto",
          display: "flex",
          flexWrap: "wrap",
          flexDirection: "row",
          paddingTop: "105px",
          paddingLeft: "13%"
        }}
      >
              {/* {this.state.allNote.length !== 0 ? null : (
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                opacity: ".3",
                alignItems: "center",
                paddingLeft: "25%",
                paddingTop: "24%"
              }}
            >
              <ArchiveOutlinedIcon style={{ fontSize: "7.5rem" }} />{" "}
              <b>Your archived notes appear here</b>{" "}
            </div>
          )} */}

        {this.state.allNote.map(note => (
          <div className="trashnoted" style={{ padding: 5 }}>
            <Card   className= {this.props.view ?("listview"):("gridview")} 
              style={{
                boxShadow:
                  " 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)",
                borderRadius: "11px",
                 backgroundColor: note.color,
                 padding: "12px"

              }}
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  padding: "7px"
                }}
              >
                <div>
                  <b>
                    {" "}
                    <TextField
                      id="standard-full-width"
                      style={{ margin: 8 }}
                      value={note.title}
                      multiline
                      fullWidth
                      margin="normal"
                      InputProps={{
                        disableUnderline: true
                      }}
                    />
                  </b>
                </div>
                <div></div>
              </div>
              <div
                style={{
                  justifyContent: "spacearound",
                  display: "flex",
                  top: "3%",
                  padding: "12px",
                  paddingTop: "11px"
                }}
              >
                <TextField
                  id="standard-full-width"
                  style={{ margin: 8 }}
                  value={note.description}
                  multiline
                  fullWidth
                  margin="normal"
                  InputProps={{
                    disableUnderline: true
                  }}
                />
              </div>

              <div className="footerarea">
                <div
                  style={{
                    justifyContent: "space-start",
                    display: "flex",
                    padding: "12px",
                    paddingBottom: "13px"
                  }}
                >
                  <DeleteNotepermanent noterestoretrash={note.id}  refresh={this.getAllNotes} />

                  <Restoretrash noterestoretrash={note.id}  refresh={this.getAllNotes} />
                </div>
              </div>
            </Card>

          </div>
        ))}

      </div>
    );
  }
}

export  default connect(mapStateToProps) (TrashNote);
