import React, { Component } from "react";
import { Card } from "@material-ui/core";
import "../Cssfile/Displaynotes.css";
import ImageOutlinedIcon from "@material-ui/icons/ImageOutlined";
import CheckBoxOutlinedIcon from "@material-ui/icons/CheckBoxOutlined";
import CreateOutlinedIcon from "@material-ui/icons/CreateOutlined";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import ArchiveOutlinedIcon from "@material-ui/icons/ArchiveOutlined";
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import ReplayOutlinedIcon from "@material-ui/icons/ReplayOutlined";
import RedoOutlinedIcon from "@material-ui/icons/RedoOutlined";
import { DisplayAllNotes } from "./Service";
import Showmoreoption from "./Showmoreoption";
import AddArchive from "./AddArchive";
import RestoreFromTrashIcon from '@material-ui/icons/RestoreFromTrash';
export class Displaynotes extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      more:false,
      trashNote: []
    };
  }



  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {
        this.setState({
          allNote: Response.data.data
        });
      })
      .catch(err => {
        console.log("error");
      });
  };


  handledisplaynote = () => {
    this.setState({ open: true });
  };
  handleclosenote = () => {
    this.setState({ open: false });
  };


  render() {
    return (
      <div
        className="displaynotemain"

        onMouseLeave={this.handleclosenote}
        onMouseEnter={this.handledisplaynote}
      >
           {this.state.allNote.map((note, index) => (
        <p>Hello, {note.title} from {note.description}!</p>



    ))}


        {

            !this.state.open ? (
              <div    className="notedis" style={{padding:5}}  > 
              <Card  
                style={{
                  width: "222px",
                  boxShadow:
                   "5px 5px 8px rgba(0,0,0,0.2)"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "12px"
                  }}


                >
                  <b> {this.props.title}</b>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    top: "3%",
                    padding: "12px",
                    paddingTop: " 9px",
                    paddingBottom: "45px"
                  }}
                >
                  {this.props.description}
                </div>
              </Card>
           </div>
            ) : (
              <div style={{padding:5}}>
              <Card
                style={{
                  width: "222px",
                  boxShadow:
                    "2px 2px 8px rgba(0,0,0,0.2)"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "12px"
                  }}
                >
                  <div> {this.props.title}</div>
                  <div>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="24"
                      height="24"
                      viewBox="0 0 24 24"
                    >
                      <path fill="none" d="M0 0h24v24H0z" />
                      <path
                        fill="#000"
                        d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                      />
                    </svg>
                  </div>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    padding: "12px"
                  }}
                >
                  {this.props.description}
                </div>

                <div>
                  <div
                    style={{
                      justifyContent: "space-around",
                      display: "flex",
                      padding: "12px",
                      paddingBottom: "13px"
                    }}
                  >

                    <PaletteOutlinedIcon />
                    <ImageOutlinedIcon />
                    <AddArchive  propsarchivenote={this.props.id} />
                    <Showmoreoption  propsdisplaynote={this.props.id} />

                  </div>
                </div>
              </Card>
              </div>
            )

     }
      </div>
    );
  }
}

export default Displaynotes;
