import React from "react";
import PropTypes from "prop-types";
import FormGroup from "@material-ui/core/FormGroup";

import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Popover from "@material-ui/core/Popover";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import AccountCircle from "@material-ui/icons/AccountCircle";
import SearchIcon from "@material-ui/icons/Search";
import AddRoundedIcon from "@material-ui/icons/AddRounded";
import { Divider, IconButton } from "@material-ui/core";

import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import Checkbox from "@material-ui/core/Checkbox";
import { DisplayAllLabel } from "./Service";
const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2
  }
});

class SimplePopover extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      labelvalue: "",
      allLable: []
    };
  }

  state = {
    anchorEl: null
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget
    });
  };

  handleClose = () => {
    this.setState({
      anchorEl: null
    });
  };

  HandlelablevalueChange = event => {
    this.setState({
      labelvalue: event.target.value
    });
    let noteid = this.props.noteid2;
    console.log("d" + noteid);
  };
  UNSAFE_componentWillMount() {
    this.getAllLabel();
  }
  getAllLabel = () => {
    console.log("lable");

    let token = localStorage.getItem("Token");
    DisplayAllLabel(token)
      .then(Response => {
        let array = [];

        Response.data.data.forEach(element => {
          console.log(element);
          array.push(element);
        });
        this.setState({
          allLable: array
        });
      })
      .catch(err => {
        console.log("Note not found");
      });
  };

  HandlelabelAddChange = () => {

    console.log();


    let token = localStorage.getItem("Token");
    let noteid=this.props.noteid2;
    console.log(noteid);

  };
  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    console.log("all label"+this.state.allLable);


    return (
<div>
        <div
          aria-owns={open ? "simple-popper" : undefined}
          onClick={this.handleClick}
        >
          Add label
              </div>
        <Popover
          style={{ left: "165.492px" }}
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "center"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "center"
          }}
        >
          {/* <Typography className={classes.typography}> */}
          <div>
            <div>Label note</div>
            <div>
              <FormControl className={classes.margin}>
                <Input
                  placeholder="Enter label name"
                  onChange={this.HandlelablevalueChange}
                  fullWidth
                  margin="normal"
                  InputProps={{
                    disableUnderline: true
                  }}
                  endAdornment={
                    <InputAdornment position="end">
                      <SearchIcon />
                    </InputAdornment>
                  }
                />
              </FormControl>
            </div>
            {this.state.allLable !== "" ? (
              <div>
                  {this.state.allLable.map(label => (
                    <div>

                        <Checkbox
                          onChange={this.handleChange}
                        />                   


                   {label.lable_title}
                    </div>
                  ))}
              </div>
            ) : null}
            <Divider />
            {this.state.labelvalue !== "" ? (
              <div>
                <IconButton onClick={this.HandlelabelAddChange}>
                  <AddRoundedIcon />
                </IconButton>
                Create
                <b
                  fullWidth
                  margin="normal"
                  InputProps={{
                    disableUnderline: true
                  }}
                >
                  "{this.state.labelvalue}"
                </b>
              </div>
            ) : null}
          </div>
          {/* </Typography> */}
        </Popover>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(SimplePopover);
