import { LIST_VIEW, GRID_VIEW,TOGGLE_VIEW } from "../Redux/actionType";

export const initialState = {
  view: false
};

const reducer = (State = initialState, action) => {
  switch (action.type) {
    case TOGGLE_VIEW:{
        return{
            ...State,
            view : !State.view
        }
    }
    case LIST_VIEW:
      return {
        ...State,
        view: true
      };

    case GRID_VIEW:
      return {
        ...State,
        view: false
      };
    default:
      return State;
  }
};

export default reducer;
