import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Popover from "@material-ui/core/Popover";
import ClearIcon from "@material-ui/icons/Clear";
import TextField from "@material-ui/core/TextField";
import CheckIcon from "@material-ui/icons/Check";
import { Divider, IconButton } from "@material-ui/core";
import LabelIcon from "@material-ui/icons/Label";
import { DisplayAllLabel } from "./Service";
import { typography } from "@material-ui/system";
import EditOutlinedIcon from "@material-ui/icons/EditOutlined";

const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2
  }
});

class SimplePopover extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      allLable: []
    };
  }

  state = {
    anchorEl: null
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget
    });
  };

  handleClose = () => {
    this.setState({
      anchorEl: null
    });
  };

  UNSAFE_componentWillMount() {
    this.getAllLabel();
  }
  getAllLabel = () => {
    console.log("lable");

    let token = localStorage.getItem("Token");
    DisplayAllLabel(token)
      .then(Response => {
        let array = [];

        Response.data.data.forEach(element => {
          console.log(element);
          array.push(element);
        });
        this.setState({
          allLable: array
        });
      })
      .catch(err => {
        console.log("Note not found");
      });
  };
  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <div
          aria-owns={open ? "simple-popper" : undefined}
          aria-haspopup="true"
          variant="contained"
          onClick={this.handleClick}
        >
          <b>Edit labels</b>
        </div>
        <Popover
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          style={{ left: "522.492px" }}
        >
          <Typography className={classes.typography}>
            <div
              style={{
                display: "flex",
                justifyContent: "space-around",
                flexDirection: "column"
              }}
            >
              <div>
                <b>Edit Labels</b>
              </div>
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-around",
                  flexDirection: "row"
                }}
              >
                <ClearIcon style={{ paddingTop: "10px" }} />
                <TextField
                  id="standard-dense"
                  placeholder="Create new label"
                  margin="dense"
                  style={{ paddingLeft: "5%" }}
                />
                <CheckIcon style={{ paddingLeft: "5%", paddingTop: "10px" }} />
              </div>
              <div

              >
                {this.state.allLable.map(label => (
                  <div 
                  style={{
                    display: "flex",
                    justifyContent: "space-around",
                  }}
                 >
                    <LabelIcon style={{ paddingTop: "10px" }} />
                    <span style={{ paddingLeft: "5%", }}>
                      {label.lable_title}
                    </span>
                    <EditOutlinedIcon />
                  </div>

                  //
                  //
                ))}
              </div>
              <Divider />
              <div
                style={{
                  display: "flex",
                  justifyContent: "flex-end"
                }}
              >
                Done
              </div>
            </div>
          </Typography>
        </Popover>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(SimplePopover);
