import {
  TOGGLE_VIEW,
  ADD_REMINDER,
  ADD_ARCHIVE,
  ADD_PIN,
  ADD_COLLABRATOR,
  ADD_COLOR
} from "../Redux/actionType";

export const initialState = {
  view:true,
  archive: false,
  reminder:"",
  color:"",
  collabrator: {}
};

const reducer = (State = initialState, action) => {
  switch (action.type) {
    case ADD_REMINDER: {
      return {
        ...State,
        reminder: action.payload
      };
    }

    case ADD_COLOR: {
      return {
        ...State,
        color: action.payload
      };
    }

    case ADD_COLLABRATOR: {
      return {
        ...State,
        collabrator: action.payload
      };
    }

    case ADD_PIN: {
      return {
        ...State,
        view: !State.view
      };
    }

    case ADD_ARCHIVE: {
      return {
        ...State,
        archive: !State.archive
      };
    }

    case TOGGLE_VIEW: {
      return {
        ...State,
        view: !State.view
      };
    }

    default:
      return State;
  }
};

export default reducer;
