import React, { Component } from "react";
import { Paper, InputBase, Card, ClickAwayListener } from "@material-ui/core";
import "../Cssfile/CreateNote.css";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import ImageOutlinedIcon from '@material-ui/icons/ImageOutlined';
import CheckBoxOutlinedIcon from '@material-ui/icons/CheckBoxOutlined';
import CreateOutlinedIcon from '@material-ui/icons/CreateOutlined';
const theme= createMuiTheme({

});

export class CreateNote extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open:false

    };
  }


  handleOpenNoteChange =()=>
    {

      this.setState({open:true})


    }
    handleCloseChange=()=>{

      this.setState({open:false})


    }





  render() {
    return (

      <ClickAwayListener onClickAway={this.handleCloseChange}>
      <div className="createnote" >



        <Paper
          style={{
            boxShadow:
              "0px 0px 5px 0px rgba(0,0,0,0.30), 0 1px 2px rgba(0,0,0,0.22)"
          }}
        >    



         {!this.state.open?
          (
          <Card>

         <span>
            <InputBase color="white" placeholder="Take a note..."  onClick={this.state.open?this.handleCloseChange:this.handleOpenNoteChange}/>
          </span>
          <span>
          <CheckBoxOutlinedIcon/>
          </span>
          <span>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="#000"
            >
              <path d="M18.64 4.75L20 6.11l-7.79 7.79-1.36-1.36 7.79-7.79m0-2c-.51 0-1.02.2-1.41.59l-7.79 7.79c-.78.78-.78 2.05 0 2.83l1.36 1.36c.39.39.9.59 1.41.59.51 0 1.02-.2 1.41-.59l7.79-7.79c.78-.78.78-2.05 0-2.83l-1.35-1.35c-.39-.4-.9-.6-1.42-.6zM7 14.25c-1.66 0-3 1.34-3 3 0 1.31-1.16 2-2 2 .92 1.22 2.49 2 4 2 2.21 0 4-1.79 4-4 0-1.66-1.34-3-3-3z" />
            </svg>
          </span>

          <span>
         < ImageOutlinedIcon/>          </span>             
          </Card>
          ):(

          <Card>
          <span>
            <InputBase color="white" placeholder="Title"  onClick={this.state.open?this.handleCloseChange:this.handleOpenNoteChange}/>
          </span> 
          <span>
            <InputBase color="white" placeholder="Take a Note"  onClick={this.state.open?this.handleCloseChange:this.handleOpenNoteChange}/>
          </span>               
          </Card> 
          )
        }

        </Paper>


      </div>
      </ClickAwayListener>

    );
  }
}

export default CreateNote;
