import React, { Component } from "react";
import "../Cssfile/ResetPassword.css";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import Input from "@material-ui/core/Input";
import FilledInput from "@material-ui/core/FilledInput";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
export class ResetPassword extends Component {
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };

  handleClickShowPassword = () => {
    this.setState(state => ({ showPassword: !state.showPassword }));
  };

  constructor(props) {
    super(props);

    this.state = {
      showPassword: false,
      password:"",
      confirmpassword:"",
      isvalid:true,
      error:{}
    };
    this.resetpasswordhandle=this.resetpasswordhandle.bind(this);
  }
  resetpasswordhandle=()=>{
    let error=this.state.error;
    let Passwordvalid=this.state.password
    let confirmpassword=this.state.confirmpassword
    if(Passwordvalid===""){

      isvalid=false;
      error["firstname"]="password is required";
    }

    if(Passwordvalid===""){

      isvalid=false;
      error["confirmpassword"]="confirm password is required";
    }

  }

  render() {
    return (
      <div className="resetPassword">
        <div className="fundoo1">
          <span style={{ color: "blue" }}>f</span>
          <span style={{ color: "red" }}>u</span>
          <span style={{ color: "orange" }}>n</span>
          <span style={{ color: "blue" }}>d</span>
          <span style={{ color: "green" }}>o</span>
          <span style={{ color: "red" }}>o</span>
        </div>
        <div className="resetPasswordtitle">
          <h2>
            <b>resetpassword</b>
          </h2>
        </div>
        <div className="resetpassword1">
          <TextField
            id="outlined-adornment-password"
            margin="dense"
            size="small"
            variant="outlined"
            type={this.state.showPassword ? "text" : "password"}
            label="Password"
            value={this.state.password}
            onChange={this.handleChange("password")}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end" sytle={{ width: "1px" }}>
                  <IconButton
                    aria-label="Toggle password visibility"
                    onClick={this.handleClickShowPassword}
                  >
                    {this.state.showPassword ? (
                      <VisibilityOff />
                    ) : (
                      <Visibility />
                    )}
                  </IconButton>
                </InputAdornment>
              )
            }}
          />
        </div>
        <span style={{color:"red"}}>{this.state.error.lastname}</span>
        <div className="password2">
          <TextField
            margin="dense"
            size="small"
            id="outlined-adornment-password"
            variant="outlined"
            type={this.state.showPassword ? "text" : "password"}
            label="confirm"
            value={this.state.password}
            onChange={this.handleChange("password")}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end" sytle={{ width: "1px" }}>
                  <IconButton
                    aria-label="Toggle password visibility"
                    onClick={this.handleClickShowPassword}
                  >
                    {this.state.showPassword ? (
                      <VisibilityOff />
                    ) : (
                      <Visibility />
                    )}
                  </IconButton>
                </InputAdornment>
              )
            }}
          />
        </div>
        <span style={{color:"red"}}>{this.state.error.confirmpassword}</span>

        <div className="Resetbutton">
          <Button variant="contained" color="primary">
            submit
          </Button>
          <Button variant="contained" color="secondary">
            Cancel
          </Button>
        </div>
      </div>
    );
  }
}

export default ResetPassword;
