import React, { Component } from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Popover from '@material-ui/core/Popover';
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import { addIntrash } from "./Service";
import { Card, Tooltip } from "@material-ui/core";

const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2,
  },
});

class SimplePopover extends React.Component {

    constructor(props) {
        super(props);

        this.state = {

        };
      }
  state = {
    anchorEl: null,
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget,
    });
  };

  handleClose = () => {
    this.setState({
      anchorEl: null,
    });
  };

  handleDeletenoteChange=()=>{

    let token = localStorage.getItem("Token");
    let noteid=this.props.propsdisplaynote;
   console.log(token);
   console.log(noteid);


    addIntrash(noteid,token).then(Response=>{

          console.log("add in trash");

    }).catch(err=>{
        console.log("error");

    })

    }








  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
         <Tooltip title=" Change color">
                     </Tooltip>




        <Popover style={{top:'18px',left:'40px',width:'150px'}}
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'center',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'center',
          }}
        >
          <Typography className={classes.typography}  style={{display:'flex',justifyContent:'start',flexDirection:'column',    width: '70px'}}>


  <h6  onClick={this.handleDeletenoteChange}>Delete note</h6>
  <h6>Add label</h6>


</Typography>
        </Popover>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimplePopover);
