import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import Switch from '@material-ui/core/Switch';
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import Avatar from "@material-ui/core/Avatar";
import { Divider, TextField, InputBase } from "@material-ui/core";
import PersonAddIcon from "@material-ui/icons/PersonAdd";
import DoneOutlinedIcon from "@material-ui/icons/DoneOutlined";

const styles = theme => ({
  form: {
    display: 'flex',
    flexDirection: 'column',
    margin: 'auto',
    width: 'fit-content',
  },
  formControl: {
    marginTop: theme.spacing.unit * 2,
    minWidth: 120,
  },
  formControlLabel: {
    marginTop: theme.spacing.unit,
  },
});
const theme = createMuiTheme({
  overrides: {
    MuiInputBase: {
      input: {
        width: "513px",
        height: "1.875em",
        paddingLeft: "8px"
      }
    },
    MuiAvatar: {
      img: {
        width: "43%",
        borderRadius: "50%",
        height: "44%"
      }
    }
  }
});

class MaxWidthDialog extends React.Component {

  state = {
    open: false,
    fullWidth: true,
    maxWidth: 'sm',
  };

  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleMaxWidthChange = event => {
    this.setState({ maxWidth: event.target.value });
  };

  handleFullWidthChange = event => {
    this.setState({ fullWidth: event.target.checked });
  };

  render() {
    const { classes } = this.props;

    return (
      <React.Fragment>

        <Dialog
          fullWidth={this.state.fullWidth}
          maxWidth={this.state.maxWidth}
          open={this.state.open}
          onClose={this.handleClose}
          aria-labelledby="max-width-dialog-title"
        >
              <MuiThemeProvider theme={theme}>
          <div
            style={{
              display: "flex",
              flexWrap: "wrap",
              width: "600px",
              flexDirection: "column"
            }}
          >
            <span
              style={{
                padding: "2%",
                fontSize: "large",
                fontFamily: "inherit",
                fontStyle: "unset"
              }}
            >
              <b>Collaborators</b>
            </span>
            <span style={{ padding: "2%" }}>
              {" "}
              <Divider />
            </span>
            <div
              style={{
                display: "flex",
                justifyContent: ""
              }}
            >
              <span>
                <Avatar
                  alt={localStorage.getItem("Name")}
                  src={localStorage.getItem("ProfilePic")}
                  style={{ width: "77px", height: "77px" }}
                />
              </span>
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  flexDirection: "column"
                }}
              >
                <span
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    flexDirection: "column",
                    marginTop: "22px"
                  }}
                >
                  <b>{name1}(Owner)</b>
                  <i>{email}</i>
                </span>
              </div>
            </div>
            <span style={{ padding: "1%" }}>
              <Avatar></Avatar>
            </span>
            <div
              style={{
                display: "flex",
                justifyContent: "flex-start",
                flexDirection: "row",
                padding: "1%"
              }}
            >
              <span>
                <Avatar>
                  <PersonAddIcon />
                </Avatar>
              </span>
              <span>
                <InputBase
                  width="600px"
                  id="standard-basic"
                  placeholder="Person or email to share with"
                  onChange={this.doneClickhandle}
                />
              </span>
              {this.state.done !== false ? (
                <span>
                  <DoneOutlinedIcon onClick={this.donenotClickhandle} />
                </span>
              ) : null}
            </div>
            <div
              style={{
                display: "flex",
                justifyContent: "flex-end ",
                flexDirection: "row",
                padding: " 3%"
              }}
            >
              <span
                style={{ marginRight: "56px" }}
                onClick={this.handleClickClose}
              >
                Cancel
              </span>
              <span>Save</span>
            </div>
          </div>
        </MuiThemeProvider>



          <DialogActions>
            <Button onClick={this.handleClose} color="primary">
              Close
            </Button>
          </DialogActions>
        </Dialog>
      </React.Fragment>
    );
  }
}

MaxWidthDialog.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MaxWidthDialog);
