import {  TOGGLE_VIEW ,ADD_REMINDER} from "../Redux/actionType";

export const initialState = {
  view: true
};

const reducer = (State = initialState, action) => {
  switch (action.type) {

     case ADD_REMINDER:

    case ADD_ARCHIVE: {
      return {
        ...State,
        view: !State.view
      };
    }

    case TOGGLE_VIEW: {
      return {
        ...State,
        view: !State.view
      };
    }

    default:
      return State;
  }
};

export default reducer;
