import React, { Component } from "react";
import { Card } from "@material-ui/core";
import "../Cssfile/Displaynotes.css";
import ImageOutlinedIcon from "@material-ui/icons/ImageOutlined";
import CheckBoxOutlinedIcon from "@material-ui/icons/CheckBoxOutlined";
import CreateOutlinedIcon from "@material-ui/icons/CreateOutlined";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import ArchiveOutlinedIcon from "@material-ui/icons/ArchiveOutlined";
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import ReplayOutlinedIcon from "@material-ui/icons/ReplayOutlined";
import RedoOutlinedIcon from "@material-ui/icons/RedoOutlined";
import { DisplayAllNotes } from "./Service";
export class Displaynotes extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false,
      allNote: []
    };
  }
  handledisplaynote = () => {
    this.setState({ open: true });
    console.log(this.state.open);
  };
  handleclosenote = () => {
    this.setState({ open: false });
    console.log(this.state.open);
  };

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {

        this.setState({
          allNote: Response.data.data
        });
      })
      .catch(err => {
        console.log("error");
      });
  };
  UNSAFE_componentWillMount() {
    this.getAllNotes();
  }
  render() {
    return (
      <div
        className="displaynotemain"

      >

        {this.state.allNote.map(note => {
          return (
            !this.state.open ? (
              <div style={{padding:5}}> 
              <Card   onMouseEnter={this.handledisplaynote}
                      onMouseLeave={this.handleclosenote}
                style={{
                  width: "222px",
                  boxShadow:
                    "11px 2px 154px 17px rgba(0,0,0,0.2), 0px 1px 1px 0px rgba(0,0,0,0.14), 0px 1px 3px 0px rgba(0,0,0,0.12)"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "12px"
                  }}
                >
                  <b> {note.title}</b>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    top: "3%",
                    padding: "12px",
                    paddingTop: " 9px",
                    paddingBottom: "45px"
                  }}
                >
                  {note.description}
                </div>
              </Card>
           </div>
            ) : (
              <div style={{padding:5}}>
              <Card
                style={{
                  width: "222px",
                  boxShadow:
                    "11px 2px 154px 17px rgba(0,0,0,0.2), 0px 1px 1px 0px rgba(0,0,0,0.14), 0px 1px 3px 0px rgba(0,0,0,0.12)"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "12px"
                  }}
                >
                  <div> {note.title}</div>
                  <div>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="24"
                      height="24"
                      viewBox="0 0 24 24"
                    >
                      <path fill="none" d="M0 0h24v24H0z" />
                      <path
                        fill="#000"
                        d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                      />
                    </svg>
                  </div>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    padding: "12px"
                  }}
                >
                  {note.description}
                </div>
                <div></div>
                <div>
                  <div
                    style={{
                      justifyContent: "space-around",
                      display: "flex",
                      padding: "12px",
                      paddingBottom: "13px"
                    }}
                  >
                    {" "}
                    <AddAlertOutlinedIcon />
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="24"
                      height="24"
                      viewBox="0 0 24 24"
                      fill="#000"
                    >
                      <path d="M9 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0-6c1.1 0 2 .9 2 2s-.9 2-2 2-2-.9-2-2 .9-2 2-2zm0 7c-2.67 0-8 1.34-8 4v3h16v-3c0-2.66-5.33-4-8-4zm6 5H3v-.99C3.2 16.29 6.3 15 9 15s5.8 1.29 6 2v1zm3-4v-3h-3V9h3V6h2v3h3v2h-3v3h-2z" />
                    </svg>
                    <PaletteOutlinedIcon />
                    <ImageOutlinedIcon />
                    <ArchiveOutlinedIcon />
                    <MoreVertOutlinedIcon />
                  </div>
                </div>
              </Card>
              </div>
            )
          )
        })}
      </div>
    );
  }
}

export default Displaynotes;
