import React, { Component } from "react";
import { Card, Tooltip, Button, Snackbar, Typography } from "@material-ui/core";
import "../Cssfile/Displaynotes.css";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import { Dialog } from "@material-ui/core";
import { DisplayAllNotes, editNote, removeReminder, pinUnpin } from "./Service";
import Showmoreoption from "./Showmoreoption";
import EmojiObjectsOutlinedIcon from "@material-ui/icons/EmojiObjectsOutlined";
import ClearIcon from '@material-ui/icons/Clear';
import AddArchive from "./AddArchive";
import "../Cssfile/TrashNote.css";
import TextField from "@material-ui/core/TextField";
import SetColor from "./SetColor";
import AddReminder from "./AddReminder";
import Chip from "@material-ui/core/Chip";
import CreateNote from "./CreateNote";
import Collaborator from "./Collaborator";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import { Redirect } from "react-router-dom";
import { withStyles } from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import { connect } from "react-redux";
const mapStateToProps = state => {
  return {
    view: state.view
  };
};

export class Displaynotes extends Component {
  queue = [];
  constructor(props) {
    super(props);
    let token = localStorage.getItem("Token");
    let loggedIn = true;
    if (token == null) {
      loggedIn = false;
    }

    this.state = {
      open: false,
      dialogOpen: false,
      more: false,
      allNote: [],
      notearr: null,
      title: "",
      description: "",
      pinUnpinArray: [],
      loggedIn,
      opensnackbar: false,
      messageInfo: {},
      labelarray: []
    };
  }

  handlesetArchive = archive => {};

  handlesetLabel = labels => {
    console.log("display" + labels);

    this.setState({
      labelarray: labels
    });
  };

  processQueue = () => {
    if (this.queue.length > 0) {
      this.setState({
        messageInfo: this.queue.shift(),
        opensnackbar: true
      });
    }
  };
  handleExited = () => {
    this.processQueue();
  };

  handleReminder = reminder => {};
  handleUpdatetitle = event => {
    this.setState({
      title: event.target.value
    });
    console.log("ddd" + this.state.title);
  };

  handleUpdatedescription = event => {
    this.setState({
      description: event.target.value
    });
    console.log(this.state.description);
  };

  handleClose1 = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    this.setState({ opensnackbar: false });
  };
  handleClose = () => {
    console.log("close ");
    this.setState({
      dialogOpen: false
    });

    let updatenote = {};
    console.log(this.state.title);
    console.log(this.state.description);

    updatenote.title = this.state.title;
    updatenote.description = this.state.description;
    let token = localStorage.getItem("Token");
    let noteid = this.state.notearr.id;

    editNote(updatenote, noteid, token)
      .then(Response => {
        let message = "Note update successfully ";
        console.log("update note");

        this.getAllNotes();
        this.queue.push({
          message,
          key: new Date().getTime()
        });
      })
      .catch(err => {
        console.log("not update note");
      });
  };

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {
        let array = [];
        let array1 = [];
        Response.data.data.forEach(element => {
          if (
            element.trash === false &&
            element.archive === false &&
            element.pin === false
          ) {
            array.push(element);
          }
          console.log(array);
          if (
            element.archive === false &&
            element.trash === false &&
            element.pin === true
          ) {
            array1.push(element);
          }
        });
        this.setState({
          allNote: array
        });

        console.log("a" + array);

        this.setState({
          pinUnpinArray: array1
        });
        console.log("b" + this.state.pinUnpinArray);
      })
      .catch(err => {
        console.log("error");
      });
  };

  handleDialogOpen = note => {
    console.log(note);
    this.setState({
      dialogOpen: !this.state.dialogOpen,
      notearr: note
    });
  };

  handlesetColor = color => {};

  handlepinunpin = noteid => {
    console.log("dd");

    let message = "pin change";

    let token = localStorage.getItem("Token");
    pinUnpin(token, noteid)
      .then(Response => {
        console.log("pinunpin done");
        this.queue.push({
          message,
          key: new Date().getTime()
        });

        this.getAllNotes();
        this.setState({ opensnackbar: true });
      })
      .catch(err => {
        console.log("not done error");
      });
  };

  handleDelete = noteid => {
    let message = "remove reminder";

    let token = localStorage.getItem("Token");
    removeReminder(noteid, token)
      .then(Response => {
        console.log("remove reminder");
        this.getAllNotes();
        this.queue.push({
          message,
          key: new Date().getTime()
        });
        this.setState({ opensnackbar: true });
      })
      .catch({
        // console.log("fail..");
      });
  };

  UNSAFE_componentWillMount() {
    this.getAllNotes();
    console.log("ddd"+this.props.drawer);
    console.log("ddd"+this.props.view);

  }

  render() {
    if (this.state.loggedIn === false) {
      return <Redirect to="/login" />;
    }
    const { messageInfo } = this.state;

        console.log(this.state.pinUnpinArray);
        console.log(this.state.allNote);



    return (
      <div 
      className={this.props.drawer ? "draweropen": null}
      >
        <div>
          <CreateNote refresh={this.getAllNotes} />
        </div>

        {this.state.allNote.length !== 0 ||
        this.state.pinUnpinArray.length !== 0 ? null : (
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              opacity: ".3",
              width: "800px",
              alignItems: "center",
              paddingTop: "19%"
            }}
          >
            <EmojiObjectsOutlinedIcon style={{ fontSize: "7.5rem" }} />{" "}
            <b>Your notes appear here</b>{" "}
          </div>
        )}

        {this.state.pinUnpinArray.length !== 0 ? (
          <div
            className={this.props.view ? "listview" : "gridview1"}
            style={{
              display: "flex",
              width: "800px",
              flexWrap: "wrap",
              flexDirection: "row",
              paddingTop: "8%",
              paddingLeft: "11%"
            }}
          >
            PINNED
          </div>
        ) : null}

        <div
          style={{
            display: "flex",
            width: "800px",
            flexWrap: "wrap",
            flexDirection: "row",
            paddingLeft: "8%"
          }}
        >
          {this.state.pinUnpinArray.map(note => (
            <div className="trashnoted" style={{ padding: 5 }}>
              <Card
                className={this.props.view ? "listview" : "gridview"}
                style={{
                  boxShadow: "2px 2px 8px rgba(0,0,0,0.2)",
                  backgroundColor: note.color,
                  borderRadius: "10px"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "2%"
                  }}
                >
                  <div>
                    <TextField
                      onClick={() => this.handleDialogOpen(note)}
                      id="standard-full-width"
                      style={{ margin: 8 }}
                      value={note.title}
                      multiline
                      fullWidth
                      margin="normal"
                      InputProps={{
                        disableUnderline: true
                      }}
                    />
                  </div>
                  <div
                    className="footerarea"
                    onClick={() => this.handlepinunpin(note.id)}
                    style={{ paddingTop: "1%" }}
                  >
                    <Tooltip title="unpin note ">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 24 24"
                      >
                        <path fill="none" d="M0 0h24v24H0z" />
                        <path
                          fill="#000"
                          d="M17 4a2 2 0 0 0-2-2H9c-1.1 0-2 .9-2 2v7l-2 3v2h6v5l1 1 1-1v-5h6v-2l-2-3V4z"
                        />
                      </svg>
                    </Tooltip>
                  </div>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    padding: "2%"
                  }}
                  onClick={() => this.handleDialogOpen(note)}
                >
                  {" "}
                  <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    value={note.description}
                    multiline
                    fullWidth
                    margin="normal"
                    InputProps={{
                      disableUnderline: true
                    }}
                  />
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    flexDirection: "row"
                  }}
                >
                  {note.reminder != null ? (
                    <div
                      style={{
                        justifyContent: "spacearound",
                        display: "flex"
                      }}
                    >
                      <Chip
                        style={{ backgroundColor: "#rgba(206, 206, 206, 0)" }}
                        label={note.reminder}
                        onDelete={() => this.handleDelete(note.id)}
                        variant="outlined"
                      />
                    </div>
                  ) : null}

                  <div
                    style={{
                      justifyContent: "spacearound",
                      display: "flex",
                      paddingLeft: "6px"
                    }}
                  >
                    {note.listOfLabels .length != 0
                      ? note.listOfLabels.map(col => (
                        console.log(col),

                        <Chip
                        size="small"
                        label={col.lable_title}
                        style={{
                        width: "95px"
                        }}
                        icon={
                        <div className="clearButton">
                        <ClearIcon
                        onClick={() =>
                        this.handleDeleteLabel(col)
                        }
                        fontSize="small"
                        />
                        </div>
                        }
                        clickable={true}
                        />


                        ))
                      : null}
                  </div>
                  <div
                    style={{
                      justifyContent: "spacearound",
                      display: "flex",
                      paddingLeft: "6px"
                    }}
                  >
                    {note.collabrators != null
                      ? note.collabrators.map(col => (
                          <div>
                            <Tooltip title={col}>
                              <AccountCircleIcon />
                            </Tooltip>
                          </div>
                        ))
                      : null}
                  </div>
                </div>

                <div className="footerarea">
                  <div
                    className={this.props.view ? "footer1" : "footer2"}

                    // style={{
                    //   justifyContent: "flex-start",
                    //   display: "flex",
                    //   padding: "12px",
                    //   paddingBottom: "13px"
                    // }}
                  >
                    <AddReminder
                      className={this.props.view ? null : "footerpadding"}
                      propsRemindernote={note.id}
                      refresh={this.getAllNotes}
                      onSelectReminder={this.handleReminder}
                    />
                    <Collaborator
                      propsRemindernote={note.id}
                      propscollbratorid={
                        note.collabrators !== null ? note.collabrators : null
                      }
                      refresh={this.getAllNotes}
                    />

                    <SetColor
                      propsColornote={note.id}
                      refresh={this.getAllNotes}
                      onSelectColor={this.handlesetColor}
                    />
                    <AddArchive
                      onSelectArchive={this.handlesetArchive}
                      propsarchivenote={note.id}
                      refresh={this.getAllNotes}
                      onClick={() => this.handlepinunpin(note.id)}
                    />
                    <Tooltip title=" More">
                      <Showmoreoption
                        propsdisplaynote={note.id}
                        onSelectLabel={this.handlesetLabel}
                        refresh={this.getAllNotes}
                      />
                    </Tooltip>
                  </div>
                </div>
              </Card>
            </div>
          ))}
        </div>
        {this.state.pinUnpinArray.length !== 0 ? (
          <div
            className={this.props.view ? "listview" : "gridview1"}
            style={{
              display: "flex",
              width: "800px",
              flexWrap: "wrap",
              flexDirection: "row",
              paddingTop: "6%",
              paddingLeft: "11%"
            }}
          >
            OTHERS
          </div>
        ) : null}

        <div
          style={{
            display: "flex",
            width: "800px",
            flexWrap: "wrap",
            flexDirection: "row",
            paddingLeft: "8%",
            paddingTop: "6%"
          }}
        >
          {this.state.allNote.map(note => (
            <div className="trashnoted" style={{ padding: 5 }}>
              <Card
                className={this.props.view ? "listview" : "gridview"}
                style={{
                  boxShadow: "2px 2px 8px rgba(0,0,0,0.2)",
                  backgroundColor: note.color,
                  borderRadius: "10px"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "2%"
                  }}
                >
                  <div>
                    <TextField
                      onClick={() => this.handleDialogOpen(note)}
                      id="standard-full-width"
                      style={{ margin: 8 }}
                      value={note.title}
