import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Popover from "@material-ui/core/Popover";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import AccountCircleOutlinedIcon from "@material-ui/icons/AccountCircleOutlined";
import Divider from "@material-ui/core/Divider";
 import  ProfileAvatar from "../component/ProfileAvatar"
const useStyles = makeStyles(theme => ({
  typography: {
    padding: theme.spacing(2)
  }
}));




export default function   SimplePopover(props) {

  let name=localStorage.getItem("Name");
  let email=localStorage.getItem("Email");
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);



  const handleClick = event => {
    console.log("ddddd");

    setAnchorEl(event.currentTarget);
  };
  const handleloginChange1 = () => {
    console.log(props);

    props.PropsDashboard.history.push("/");
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);
  const id = open ? "simple-popover" : null;

  return (
    <div>

      <ProfileAvatar  onClick={handleClick}  />
      {/* <AccountCircleOutlinedIcon onClick={handleClick} /> */}


      <Popover
        style={{ top: "5%" }}
        id={id}
        open={open}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "right"
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "right"
        }}
      >
        <Typography className={classes.typography}>
          <div className="mainprofle">
            <div
              style={{ justifyContent: "center", display: "flex", top: "%" }}
            >

              <ProfileAvatar   />
            </div>
            <div
              style={{ justifyContent: "center", display: "flex", top: "3%" }}
            >
              <b> {name}
               </b>
            </div>
            <div
              style={{
                justifyContent: "center",
                display: "flex",
                top: "2%",
                padding: "2%"
              }}
            >


              {email}
            </div>
            <div
              className="profileaccount"
              style={{
                backgroundColor: "#e0e0e0",
                padding: "3% ",
                paddingBottom: "3%"
              }}
            >
              Manage Your Fundoo Account
            </div>
            <Divider />
            <div
              style={{
                justifyContent: "center",
                display: "flex",
                padding: "9%"
              }}
            >
              <Button
                variant="contained"
                color="white"
                style={{ justifyContent: "center", display: "flex" }}
                onClick={handleloginChange1}
              >
                sign out
              </Button>
            </div>
            <Divider />
            <div className="profilefooter">
              {" "}
              Privacy Policy . Terms of Service
            </div>
          </div>
        </Typography>
      </Popover>
    </div>
  );
}

