import {TOGGLE_VIEW,ADD_REMINDER,ADD_COLOR} from '../Redux/actionType'

export const toggleView = () =>{
    return{
        type:TOGGLE_VIEW
    }
}


export const addReminder=(reminder)=>{
    return{
        type:ADD_REMINDER,
        payload:reminder
    }
}
export const addColor=(color)=>{
    return{
        type:ADD_COLOR,
        payload:color
    }
}
