import {TOGGLE_VIEW,ADD_REMINDER,ADD_COLOR,ADD_COLLABRATOR,ADD_PIN} from '../Redux/actionType'

export const toggleView = () =>{
    return{
        type:TOGGLE_VIEW
    }
}


export const addReminder=(reminder)=>{
    return{
        type:ADD_REMINDER,
        payload:reminder
    }
}
