import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Popover from "@material-ui/core/Popover";
import "../Cssfile/addReminder.css";
import AccessTimeIcon from "@material-ui/icons/AccessTime";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import Datetime from "./Datetime";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";

import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker
} from "@material-ui/pickers";
const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2
  }
});

class SimplePopover extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
       date:new Date(),
       time:new Date()
    }
  }


  state = {
    anchorEl: null
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget
    });
  };

  handleClose = () => {
      console.log("close");

    this.setState({
      anchorEl: null
    });
  };

   handleDateChange = date => {
    setSelectedDate(date);
    console.log("userdate" + date);
  };
   handleTimeChange = time => {
    SetSelectedTime(time);
    console.log("usertime " + time);
  };

  render() {
    const { selectedDate } = this.state;

    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <div onClick={this.handleClick} className="pickdatetime">

          <AccessTimeIcon
            fontSize="inherit"
            style={{ position: "fixed" }}

          />
          <span style={{ marginLeft: "32px" }}> Pick date & time</span>
        </div>
        <Popover
          style={{ top: "-150px", borderRadius: "4%" }}
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "center"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "center"
          }}
        >
          <div className="main">
            <ArrowBackIcon style={{ position: "fixed", fontSize: "22px" }} />
            <span style={{ marginLeft: "32px" }}> Pick date & time</span>
          </div>

          <Datetime />


          <div
            onClick={this.handleClose}
            style={{
              display: "flex",
              justifyContent: "flex-end",
              padding: "4%"
            }}
          >
            Save
          </div>
        </Popover>
      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(SimplePopover);
