import {
  TOGGLE_VIEW,LABEL_ID,DRAWER,ADD_COLOR

} from "../Redux/actionType";

export const initialState = {
  view:true,
  labelid:'',
  drawer:false

};

const reducer = (State = initialState, action) => {
  switch (action.type) {


    case LABEL_ID: {
      return {
        ...State,
        labelid: action.payload
      };
    }

    case LABEL_ID: {
      return {
        ...State,
        labelid: action.payload
      };
    }

    case DRAWER: {
      return {
        ...State,
        drawer: !State.drawer
      };
    }


    case TOGGLE_VIEW: {
      return {
        ...State,
        view: !State.view
      };
    }

    default:
      return State;
  }
};

export default reducer;
