import React, { Component } from "react";
import "../Cssfile/Dashboard.css";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import InputBase from "@material-ui/core/InputBase";
import keeplogo from "../image/keeplogo.png";
import keep1 from "../image/1.svg";
import SearchIcon from "@material-ui/icons/Search";
import Drawer from "./Drawer";
import SettingsOutlinedIcon from "@material-ui/icons/SettingsOutlined";
import RefreshOutlinedIcon from "@material-ui/icons/RefreshOutlined";
import ProfileAvatar from "../component/ProfileAvatar";
import Profile from "../component/Profile";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import { Avatar } from "@material-ui/core";
import MyDrawer from "./MyDrawer";
import CreateNote from "./CreateNote";
import { DisplayAllNotes } from "./Service";
import Displaynotes from "./Displaynotes";

const theme = createMuiTheme({
  overrides: {
    MuiPaper: {
      elevation4: {
        // boxShadow:
          // "0px 3px 0px -3px rgba(0,0,0,0.2), 0px 4px 5px 0px rgba(0,0,0,0.14), 0px 1px 10px 0px rgba(0,0,0,0.12)"
      }
    },

    MuiDrawer: {
      paper: {
        top: "13%"
      }
    },

    PersistentDrawerLeft: {
      drawer: {
        width: "0px"
      }
    }
  }
});

class Dashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      anchorEl: null,
      userprofle: false,
      allNote: []
    };
  }


  currentClick=(currentstate)=>{
    if(currentstate.allNote===true){
      console.log("all note");

    }
  }

  handleshowprofile = () => {
    this.setState({ userprofle: true });
  };

  handlehideprofile = () => {
    this.setState({ userprofle: false });
  };

  getAllNotes = () => {
    let token = localStorage.getItem("Token");

    DisplayAllNotes(token)
      .then(Response => {
        this.setState({
          allNote: Response.data.data
        });
      })
      .catch(err => {
        console.log("error");
      });
  };
  UNSAFE_componentWillMount() {
    this.getAllNotes();
  }

  render() {
    console.log(this.props,"dashboeard props");
    console.log(this.state.allNote);

    return (
      <div className="mainDashboard">
        <MuiThemeProvider theme={theme}>
          <AppBar position="fixed">
            <Toolbar className="toolbar" style={{ backgroundColor: "white" }}>
              <div className="keepAndLogo">
                <div>
                  <MyDrawer />
                </div>
                <img
                  src={keeplogo}
                  width="50px"
                  height="50px"
                  alt="hello"
                ></img>
                <Typography
                  className="keep"
                  variant="h6"
                  color="inherit"
                  noWrap
                >
                  <b style={{ marginLeft: "2%" }}>Fundoo</b>
                </Typography>
              </div>

              <div className="dashboardSearch">
                <div className="dashboardsearchicon">
                  <SearchIcon />
                </div>
                <div style={{ width: "90%" }}>
                  <InputBase
                    color="white"
                    placeholder="Searchâ€¦"
                    style={{
                      justifyContent: "start",
                      display: "flex",
                      top: "13%"
                    }}
                  />
                </div>
              </div>

              <div className="logokeep1">
                <RefreshOutlinedIcon />
                <img src={keep1} width="22px" height="22px" alt="hello"></img>
                <SettingsOutlinedIcon />
              </div>

              <div>
                <Profile PropsDashboard={this.props} />
              </div>
            </Toolbar>
          </AppBar>
        </MuiThemeProvider>

        <div className="notesDiv">
          <div >
            <CreateNote />
          </div>
          <div className="displaynotess">
            {this.state.allNote.map((value, index) => (
              <Displaynotes
                title={this.state.allNote[index].title}
                description={this.state.allNote[index].description}
                id={this.state.allNote[index].id}

              />
            ))}{" "}
          </div>
        </div>
      </div>
    );
  }
}

export default Dashboard;
