import React from "react";
import Popover from "@material-ui/core/Popover";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Divider from "@material-ui/core/Divider";
import { Avatar } from "@material-ui/core";
import { Grid } from "@material-ui/core";
import { makeStyles, withStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  typography: {
    padding: theme.spacing(2)
  }
}));

export default function SimplePopover(props) {
  let name = localStorage.getItem("Name");
  let email = localStorage.getItem("Email");
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };
  const handleloginChange1 = () => {
    console.log(props);

    props.PropsDashboard.history.push("/");
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  // HandleOpenFileChange=()=>{

  // }
  const SmallAvatar = withStyles(theme => ({
    root: {
      width: 22,
      height: 22,
      border: `2px solid ${theme.palette.background.paper}`,
    },
  }))(Avatar);

  const open = Boolean(anchorEl);
  const id = open ? "simple-popover" : null;

  return (
    <div>
      <div style={{ display: "flex", flexDirection: "row" }}>
        <Avatar
          alt={name}
          src={localStorage.getItem("ProfilePic")}
          onClick={handleClick}
        />
      </div>
      <Popover
        style={{ top: "5%" }}
        id={id}
        open={open}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "right"
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "right"
        }}
      >
        <Typography className={classes.typography}>
          <div className="mainprofle">
            <div
              style={{ justifyContent: "center", display: "flex", top: "3%" }}
            >

                {/* <Avatar  
                  alt={localStorage.getItem("Name")}
                  src={localStorage.getItem("ProfilePic")}
                   style={{width:'77px', height:'77px'}} 
                  //  onClick={this.HandleOpenFileChange}
                /> */}
                    <Badge
        overlap="circle"
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'right',
        }}
        badgeContent={<SmallAvatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />}
      >
        <Avatar alt="Travis Howard" src="/static/images/avatar/2.jpg" />
      </Badge>

            </div>

            <div
              style={{ justifyContent: "center", display: "flex", top: "3%" }}
            >
              <b> {name}</b>
            </div>
            <div
              style={{
                justifyContent: "center",
                display: "flex",
                top: "2%",
                padding: "2%"
              }}
            >
              {email}
            </div>
            <div
              className="profileaccount"
              style={{
                backgroundColor: "#e0e0e0",
                padding: "3% ",
                paddingBottom: "3%",
                borderradius: "10px"
              }}
            >
              Manage Your Fundoo Account
            </div>
            <Divider />
            <div
              style={{
                justifyContent: "center",
                display: "flex",
                padding: "9%"
              }}
            >
              <Button
                variant="contained"
                color="white"
                style={{ justifyContent: "center", display: "flex" }}
                onClick={handleloginChange1}
              >
                sign out
              </Button>
            </div>
            <Divider />
            <div className="profilefooter">
              {" "}
              Privacy Policy . Terms of Service
            </div>
          </div>
        </Typography>
      </Popover>
    </div>
  );
}
