import React from "react";
import PropTypes from "prop-types";
import FormGroup from "@material-ui/core/FormGroup";

import { withStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Popover from "@material-ui/core/Popover";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import AccountCircle from "@material-ui/icons/AccountCircle";
import SearchIcon from "@material-ui/icons/Search";
import AddRoundedIcon from "@material-ui/icons/AddRounded";
import { Divider, IconButton } from "@material-ui/core";

import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import Checkbox from "@material-ui/core/Checkbox";
import { DisplayAllLabel } from "./Service";
const styles = theme => ({
  typography: {
    margin: theme.spacing.unit * 2
  }
});

class SimplePopover extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      labelvalue: "",
      allLable: []
    };
  }

  state = {
    anchorEl: null
  };

  handleClick = event => {
    this.setState({
      anchorEl: event.currentTarget
    });
  };

  handleClose = () => {
    this.setState({
      anchorEl: null
    });
  };

  HandlelablevalueChange = event => {
    this.setState({
      labelvalue: event.target.value
    });
    let noteid = this.props.noteid2;
    console.log("d" + noteid);
  };
  UNSAFE_componentWillMount() {
    this.getAllLabel();
  }
  getAllLabel = () => {
    console.log("lable");

    let token = localStorage.getItem("Token");
    DisplayAllLabel(token)
      .then(Response => {
        let array = [];

        Response.data.data.forEach(element => {
          console.log(element);
          array.push(element);
        });
        this.setState({
          allLable: array
        });
      })
      .catch(err => {
        console.log("Note not found");
      });
  };

  HandlelabelAddChange = () => {
    console.log("ass" + this.state.allLable);

    let token = localStorage.getItem("Token");
  };
  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    console.log("all label"+this.state.allLable);


    return (
      <div>
                 <Dialog
          open={this.state.dialogOpen}
          onClose={this.handleClose}
          aria-labelledby="simple-dialog-title"
          style={{ width: "100%" }}
        >
          {this.state.notearr !== null ? (
            <div className="trashnoted" style={{ padding: 5 }}>
              <Card
                style={{
                  width: "484px",
                  boxShadow: "2px 2px 8px rgba(0,0,0,0.2)"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding: "12px"
                  }}
                >
                  <div>
                    <TextField
                      id="standard-full-width"
                      style={{ margin: 8 }}
                      defaultValue={this.state.notearr.title}
                      multiline
                      fullWidth
                      margin="normal"
                      InputProps={{
                        disableUnderline: true
                      }}
                      onChange={this.handleUpdatetitle}
                    />
                  </div>
                  <div className="footerarea">
                    <Tooltip title="pin note ">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 24 24"
                      >
                        <path fill="none" d="M0 0h24v24H0z" />
                        <path
                          fill="#000"
                          d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                        />
                      </svg>
                    </Tooltip>
                  </div>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    padding: "12px"
                  }}
                >
                  <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    defaultValue={this.state.notearr.description}
                    multiline
                    fullWidth
                    margin="normal"
                    InputProps={{
                      disableUnderline: true
                    }}
                    onChange={this.handleUpdatedescription}
                  />
                </div>

                <div className="footerarea">
                  <div
                    style={{
                      justifyContent: "space-around",
                      display: "flex",
                      padding: "12px",
                      paddingBottom: "13px"
                    }}
                  >
                    <Tooltip title="Remind me">
                      <AddAlertOutlinedIcon />
                    </Tooltip>
                    <Tooltip title="Collaborator">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 24 24"
                        fill="#000"
                      >
                        <path d="M9 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0-6c1.1 0 2 .9 2 2s-.9 2-2 2-2-.9-2-2 .9-2 2-2zm0 7c-2.67 0-8 1.34-8 4v3h16v-3c0-2.66-5.33-4-8-4zm6 5H3v-.99C3.2 16.29 6.3 15 9 15s5.8 1.29 6 2v1zm3-4v-3h-3V9h3V6h2v3h3v2h-3v3h-2z" />
                      </svg>
                    </Tooltip>
                    <Tooltip title=" Change color">
                      <PaletteOutlinedIcon />
                    </Tooltip>
                    <AddArchive propsarchivenote={this.state.notearr.id} />
                    <Tooltip title=" More">
                      <Showmoreoption
                        propsdisplaynote={this.state.notearr.id}
                      />
                    </Tooltip>
                    <Button onClick={this.handleClose}>close</Button>
                  </div>
                </div>
              </Card>
            </div>
          ) : null}
        </Dialog>

      </div>
    );
  }
}

SimplePopover.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(SimplePopover);
