import React, { Component } from "react";
import { Paper, InputBase, Card, ClickAwayListener } from "@material-ui/core";
import "../Cssfile/CreateNote.css";
import { MuiThemeProvider, createMuiTheme } from "@material-ui/core/styles";
import ImageOutlinedIcon from "@material-ui/icons/ImageOutlined";
import CheckBoxOutlinedIcon from "@material-ui/icons/CheckBoxOutlined";
import CreateOutlinedIcon from "@material-ui/icons/CreateOutlined";
import AddAlertOutlinedIcon from "@material-ui/icons/AddAlertOutlined";
import PaletteOutlinedIcon from "@material-ui/icons/PaletteOutlined";
import ArchiveOutlinedIcon from "@material-ui/icons/ArchiveOutlined";
import MoreVertOutlinedIcon from "@material-ui/icons/MoreVertOutlined";
import ReplayOutlinedIcon from "@material-ui/icons/ReplayOutlined";
import RedoOutlinedIcon from "@material-ui/icons/RedoOutlined";
const theme = createMuiTheme({});

export class CreateNote extends Component {
  constructor(props) {
    super(props);

    this.state = {
      open: false
    };
  }

  handleOpenNoteChange = () => {
    this.setState({ open: true });
  };
  handleCloseChange = () => {
    this.setState({ open: false });
  };

  render() {
    return (
      <ClickAwayListener onClickAway={this.handleCloseChange}>
        <div className="createnote">
          <Paper
            style={{
              width: "492px",
              boxShadow:
                "0px 0px 5px 0px rgba(0,0,0,0.30), 0 1px 2px rgba(0,0,0,0.22)"
            }}
          >
            {!this.state.open ? (
              <Card>
                <div
                  style={{
                    justifyContent: "space-around",
                    display: "flex",
                    paddingTop: "6px",
                    paddingBottom: "6px"
                  }}
                >
                  <InputBase
                    color="white"
                    placeholder="Take a note..."
                    onClick={
                      this.state.open
                        ? this.handleCloseChange
                        : this.handleOpenNoteChange
                    }
                  />
                  <CheckBoxOutlinedIcon />
                  <CreateOutlinedIcon />
                  <ImageOutlinedIcon />
                </div>
              </Card>
            ) : (
              <Card>
                <div
                  style={{
                    justifyContent: "space-between",
                    display: "flex",
                    paddingLeft: "13px",
                    paddingRight: "13px",
                    paddingTop: "13px"
                  }}
                >
                  <InputBase color="white" placeholder="Title" />
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                  >
                    <path fill="none" d="M0 0h24v24H0z" />
                    <path
                      fill="#000"
                      d="M17 4v7l2 3v2h-6v5l-1 1-1-1v-5H5v-2l2-3V4c0-1.1.9-2 2-2h6c1.11 0 2 .89 2 2zM9 4v7.75L7.5 14h9L15 11.75V4H9z"
                    />
                  </svg>
                </div>

                <div
                  style={{
                    justifyContent: "space-between",
                    display: "flex",
                    paddingLeft: "13px",
                    paddingTop: "13px"
                  }}
                >
                  <InputBase color="white" placeholder="Take a Note..." />
                </div>

                <div
                  style={{
                    justifyContent: "space-around",
                    display: "flex",
                    paddingBottom: "13px"
                  }}
                >
                  {" "}
                  <AddAlertOutlinedIcon />
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="#000"
                  >
                    <path d="M9 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0-6c1.1 0 2 .9 2 2s-.9 2-2 2-2-.9-2-2 .9-2 2-2zm0 7c-2.67 0-8 1.34-8 4v3h16v-3c0-2.66-5.33-4-8-4zm6 5H3v-.99C3.2 16.29 6.3 15 9 15s5.8 1.29 6 2v1zm3-4v-3h-3V9h3V6h2v3h3v2h-3v3h-2z" />
                  </svg>
                  <PaletteOutlinedIcon />
                  <ImageOutlinedIcon />
                  <ArchiveOutlinedIcon />
                  <MoreVertOutlinedIcon />
                  <b onClick={this.handleCloseChange}>close</b>
                </div>
              </Card>
            )}
          </Paper>
        </div>
      </ClickAwayListener>
    );
  }
}

export default CreateNote;
