import React, { Component } from 'react'
import PropTypes from 'prop-types'
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import '../Cssfile/Login.css'
import IconButton from '@material-ui/core/IconButton';
import Input from '@material-ui/core/Input';
import FilledInput from '@material-ui/core/FilledInput';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import { userLogin } from './Service';
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator';


export class Login extends Component {

  state = {
    email: '',
}
  constructor(props) {
    super(props)

    this.state = {
        showPassword: false,
        userName:"",
        userPassword:""


    }
}

  handleUsernameChange=event=>{
    this.setState({
      userName:event.target.value

    });
    console.log(this.state.userName)

  }
  handleloginChange =() =>{
   let userlogin={};
   userlogin.userName=this.state.userName;
   userlogin.userPassword=this.state.userPassword;
   userLogin(userlogin)
    .then(Response=>{
      alert(`${Response.data.message}`)
      console.log(Response,"user login successfully")
    })
    .catch(err=>{
      console.log(Response,"user login fail")
    })
  }


  handleloginpasswordchange= event =>{
    this.setState({
      userPassword:event.target.value
    });
    console.log(this.state.userPassword)

  }

  forgotpassword=()=>{
    this.props.history.push("/ForgotPassword")
  }
  handleregister= () =>{
    this.props.history.push("/register")

  }
    handleChange = prop => event => {
        this.setState({ [prop]: event.target.value });
      };

      handleClickShowPassword = () => {
        this.setState(state => ({ showPassword: !state.showPassword }));
      };






    render() {
      const { email } = this.state;
        return (
            <div className="login">
              {/*  */}
                <div className="fundooLogin"> 
                 <span style={{ color: "blue" }}>f</span>
                 <span style={{ color: "red" }}>u</span>
                 <span style={{ color: "orange" }}>n</span>
                 <span style={{ color: "blue" }}>d</span>
                 <span style={{ color: "green" }}>o</span>
                 <span style={{ color: "red" }}>o</span>
               </div>
                <div className="signInLogin"> <span >Sign in</span></div>

                <div className="usernameLogin"> <TextField
                    margin="dense"
                    size="small"
                    name="username"                    
                    id="outlined-required"
                    label="username"
                    variant="outlined"


                    onChange={this.handleUsernameChange}
                />
                </div> 
                <div className="passwordLogin" >
                   <TextField
                   style={{width:'100%'}}
                    margin="dense"
                    size="small"

          id="outlined-adornment-password"
        //   className={classNames(classes.margin, classes.textField)}
          variant="outlined"
          type={this.state.showPassword ? 'text' : 'password'}
          label="password"
          value={this.state.password}
          onChange={this.handleChange('password')}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end" sytle={{width:'1px'}}>
                <IconButton
                  aria-label="Toggle password visibility"
                  onClick={this.handleClickShowPassword}
                >
                  {this.state.showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            ),
          }}
          onChange={this.handleloginpasswordchange}   />
        </div>
                <div className="buttonLogin">
                    <Button variant="contained" color="primary" onClick={this.handleloginChange} >
                        Login
                    </Button>
                    <Button variant="contained" color="secondary" onClick={this.handleregister}>
                        Register
</Button></div>
              <div className="Forgotpassword">  <Button onClick={this.forgotpassword}>Forgot Password?</Button></div>
            </div>
        )
    }
}

export default Login
