import React from 'react';
import PropTypes from 'prop-types';
import Popover from '@material-ui/core/Popover';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import ArchiveOutlinedIcon from "@material-ui/icons/ArchiveOutlined";
import { addInArchive } from './Service';


const styles = theme => ({
  popover: {
    pointerEvents: 'none',
  },
  paper: {
    padding: theme.spacing.unit,
  },
});

class MouseOverPopover extends React.Component {
  state = {
    anchorEl: null,
  };

  handlePopoverOpen = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handlePopoverClose = () => {
    this.setState({ anchorEl: null });
  };
   handleaddArchiveChange=()=>{

    let token = localStorage.getItem("Token");
    let noteid=this.props.propsarchivenote;

       addInArchive(noteid,token).then(Response=>{
           console.log("add in archive");

       }).catch(err=>{
          console.log("error");

       });



   }

  render() {
    const { classes } = this.props;
    const { anchorEl } = this.state;
    const open = Boolean(anchorEl);

    return (
      <div>
        <Typography
          aria-owns={open ? 'mouse-over-popover' : undefined}
          aria-haspopup="true"
          onMouseEnter={this.handlePopoverOpen}
          onMouseLeave={this.handlePopoverClose}
          onClick={this.handleaddArchiveChange}
        >
            <Tooltip title=" Change color">
            <ArchiveOutlinedIcon />                  </Tooltip>

        </Typography>




      </div>
    );
  }
}

MouseOverPopover.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(MouseOverPopover);
