import { LIST_VIEW, GRID_VIEW,TOGGLE_VIEW ,} from "../Redux/actionType";

export const initialState = {
  view: true
};

const reducer = (State = initialState, action) => {
  switch (action.type) {

      case  ADD_ARCHIVE:{
        return {}
      }
    case TOGGLE_VIEW:{
        return{
            ...State,
            view : !State.view
        }
    }

    default:
      return State;
  }
};

export default reducer;
