import React, { Component } from "react";
import { labelWithNote } from "./Service";
import { Card, Tooltip, Button, Snackbar, Typography } from "@material-ui/core";
import TextField from "@material-ui/core/TextField";
import SetColor from "./SetColor";
import AddReminder from "./AddReminder";
import Chip from "@material-ui/core/Chip";
import CreateNote from "./CreateNote";
import Collaborator from "./Collaborator";
class Displaynotewithlabel extends Component {
  constructor(props) {
    super(props);

    this.state = {
      label: {},
      labelwithnote: []
    };
  }
  getAllLabelWithNotes = () => {
    let token = localStorage.getItem("Token");
    labelWithNote(this.state.label, token).then(Response => {
      console.log("label with notes");
      let array = [];

      Response.data.data.forEach(element => {
        array.push(element);
      });
      this.setState({
        labelwithnote: array
      });
    });
  };

  componentDidMount() {
    this.setState({
      label: this.props.location.state.label.id
    });
    this.getAllLabelWithNotes();
  }
  render() {
    console.log(this.state.label);

    return (
      <div>
        <div
          style={{
            display: "flex",
            width: "800px",
            flexWrap: "wrap",
            flexDirection: "row",
            paddingLeft: "8%"
          }}  
        >
          {this.state.pinUnpinArray.map(note => (
            <div    className= "trashnoted"  style={{ padding: 5 }}>
              <Card 
              className= {this.props.view ?("listview"):("gridview")} 
                style={{
                  boxShadow: "2px 2px 8px rgba(0,0,0,0.2)",
                  backgroundColor: note.color,
                  borderRadius: "10px"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    padding:'2%'

                  }}
                >
                  <div>
                    <TextField
                      onClick={() => this.handleDialogOpen(note)}
                      id="standard-full-width"
                      style={{ margin: 8 }}
                      value={note.title}
                      multiline
                      fullWidth
                      margin="normal"
                      InputProps={{
                        disableUnderline: true
                      }}
                    />
                  </div>
                  <div
                    className="footerarea"
                    onClick={() => this.handlepinunpin(note.id)}
                    style={{paddingTop:'1%'}}
                  >
                    <Tooltip title="unpin note ">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 24 24"
                      >
                        <path fill="none" d="M0 0h24v24H0z" />
                        <path
                          fill="#000"
                          d="M17 4a2 2 0 0 0-2-2H9c-1.1 0-2 .9-2 2v7l-2 3v2h6v5l1 1 1-1v-5h6v-2l-2-3V4z"
                        />
                      </svg>
                    </Tooltip>
                  </div>
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    padding:'2%'
                  }}
                  onClick={() => this.handleDialogOpen(note)}
                >
                  {" "}
                  <TextField
                    id="standard-full-width"
                    style={{ margin: 8 }}
                    value={note.description}
                    multiline
                    fullWidth
                    margin="normal"
                    InputProps={{
                      disableUnderline: true
                    }}
                  />
                </div>
                <div
                  style={{
                    justifyContent: "spacearound",
                    display: "flex",
                    flexDirection: "row"
                  }}
                >
                  {note.reminder != null ? (
                    <div
                      style={{
                        justifyContent: "spacearound",
                        display: "flex"
                      }}
                    >
                      <Chip
                        style={{ backgroundColor: "#rgba(206, 206, 206, 0)" }}
                        label={note.reminder}
                        onDelete={() => this.handleDelete(note.id)}
                        variant="outlined"
                      />
                    </div>
                  ) : null}
                  <div
                    style={{
                      justifyContent: "spacearound",
                      display: "flex",
                      paddingLeft: "6px"
                    }}
                  >
                    {note.collabrators != null
                      ? note.collabrators.map(col => (
                          <div>
                            <Tooltip title={col}>
                              <AccountCircleIcon />
                            </Tooltip>
                          </div>
                        ))
                      : null}
                  </div>
                </div>

                <div className="footerarea">
                  <div  className= {this.props.view ?("footer1"):("footer2")} 

                    // style={{
                    //   justifyContent: "flex-start",
                    //   display: "flex",
                    //   padding: "12px",
                    //   paddingBottom: "13px"
                    // }}
                  >
                    <AddReminder
                       className= {this.props.view ?(null):("footerpadding")}
                      propsRemindernote={note.id}
                      refresh={this.getAllNotes}
                    />
                    <Collaborator
                      propsRemindernote={note.id}
                      propscollbratorid={
                        note.collabrators !== null ? note.collabrators : null
                      }
                      refresh={this.getAllNotes}
                    />

                    <SetColor
                      propsColornote={note.id}
                      refresh={this.getAllNotes}
                    />
                    <AddArchive
                      propsarchivenote={note.id}
                      refresh={this.getAllNotes}
                      onClick={() => this.handlepinunpin(note.id)}                    />
                    <Tooltip title=" More">
                      <Showmoreoption
                        propsdisplaynote={note.id}
                        refresh={this.getAllNotes}
                      />
                    </Tooltip>
                  </div>
                </div>
              </Card>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default Displaynotewithlabel;
